import React, { Component } from 'react'
import _ from 'lodash'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Checkbox } from '@/components/checkBox'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'

class StepOne extends Component {
    state = {
        name: '',
        nameError: false,
        steps: ['Channel', 'Message', 'Segmentation', 'Scheduling', 'Overview'],
        deviceType: [
            {
                name: 'IOS',
                checked: false,
            },
            {
                name: 'Android',
                checked: false,
            },
            {
                name: 'Web',
                checked: false,
            },
        ],
        deviceTypeError: false,
        type: [
            {
                name: 'General Campaign',
                checked: false,
            },
            {
                name: 'Trigger Campaign',
                checked: false,
            },
        ],
        typeError: false,
    }

    saveAndContinue = e => {
        e.preventDefault()
        this.props.data.push(this.state)
        this.props.nextStep()
    }

    _checkHasError = data => {
        let anyChecked = false

        _.forEach(data, part => {
            if (part.checked) {
                anyChecked = true
            }
        })

        return anyChecked
    }

    handleContinue = () => {
        const { name, deviceType, type } = this.state

        // let anyError = false

        if (name === '') {
            this.setState({
                nameError: true,
            })
            anyError = true
        }

        if (!this._checkHasError(deviceType)) {
            this.setState({
                deviceTypeError: true,
            })
            anyError = true
        }

        if (!this._checkHasError(type)) {
            this.setState({
                typeError: true,
            })
            anyError = true
        }

        if (!anyError) {
            this.props.onSubmit({
                name,
                deviceType,
                type,
            })
        }
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    handleCheckbox = check => {
        const { deviceType } = this.state
        this.setState({
            deviceType: deviceType.map(part => {
                if (part.name === check.name) {
                    part.checked = !check.checked
                }
                return part
            }),
        })
    }

    handleRadio = check => {
        const { type } = this.state
        this.setState({
            type: type.map(part => {
                if (part.name === check.name) {
                    part.checked = true
                } else {
                    part.checked = false
                }
                return part
            }),
        })
    }

    render() {
        const { steps, deviceType, type, deviceTypeError, nameError } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={0} />
                <div className="section">
                    <SmallHeading>Campaign Name</SmallHeading>
                    {nameError && <div className="alert">*this is mandatory field</div>}
                    <input
                        placeholder="Enter Campaign Name"
                        type="text"
                        className="form-input"
                        name="name"
                        value={this.state.name}
                        onChange={e => this.handleInputFields('name', e.target.value)}
                    />
                </div>
                <div className="section">
                    <SmallHeading>Platforms</SmallHeading>
                    {deviceTypeError && <div className="alert">*this is mandatory field</div>}

                    <div className="mrgn-top-15">
                        {deviceType.map(part => {
                            return (
                                <Checkbox
                                    key={part.name}
                                    onClick={() => this.handleCheckbox(part)}
                                    name="device"
                                    id={part.name}
                                    checked={part.checked}
                                >
                                    {part.name}
                                </Checkbox>
                            )
                        })}
                    </div>
                </div>
                <div className="section">
                    <SmallHeading>Campaign Type</SmallHeading>
                    <div className="">
                        {type.map(part => (
                            <Radio
                                onClick={() => this.handleRadio(part)}
                                key={part.name}
                                checked={part.checked}
                                id={part.name}
                            >
                                {part.name}
                            </Radio>
                        ))}
                    </div>
                </div>
                <ColoredButtons onClick={this.handleContinue} className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons onClick={this.back} className="float">
                    back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default StepOne
