import React, { Component } from 'react'
import Select from 'react-select'
import { ColoredButtons } from '@/components/button/index'
import './index.scss'
import { eventValues, operators, attributeNames } from './defaultData'

class CreateRuleImport extends Component {
    state = {
        addProperty: false,
    }
    render() {
        const { addProperty } = this.state
        return (
            <div className="section mrgn-top-15">
                <div
                    className="flex width-150"
                    style={{
                        alignItems: 'center',
                    }}
                >
                    <ColoredButtons onClick={() => this.setState({ addProperty: !addProperty })}>
                        Add Property
                    </ColoredButtons>
                    <ColoredButtons onClick={() => this.setState({ addProperty: !addProperty })}>
                        Add Actions
                    </ColoredButtons>
                    <Select className="width-150" placeholder="Select App" />
                </div>
                {addProperty ? (
                    <div key={index} className="flex-space-bt filter mrgn-top-15">
                        <Select
                            className="trigger-select"
                            // value={}
                            options={attributeNames}
                            // onChange={v =>
                            //     this.handleOnChange(
                            //         `primaryAction.attributeSection[${index}].attributeName`,
                            //         v
                            //     )
                            // }
                            theme={selectTheme}
                            placeholder="Attribute Name"
                        />
                        <Select
                            className="trigger-select"
                            // value={_.get(part, 'operator')}
                            options={operators}
                            // onChange={v =>
                            //     this.handleOnChange(
                            //         `primaryAction.attributeSection[${index}].operator`,
                            //         v
                            //     )
                            // }
                            theme={selectTheme}
                            placeholder="Operator"
                        />
                        <Select
                            className="trigger-select"
                            // value={_.get(part, 'value')}
                            options={eventValues}
                            // onChange={v =>
                            //     this.handleOnChange(
                            //         `primaryAction.attributeSection[${index}].value`,
                            //         v
                            //     )
                            // }
                            theme={selectTheme}
                            placeholder="Value"
                        />
                    </div>
                ) : (
                    <>bye :) </>
                )}
            </div>
        )
    }
}

export default CreateRuleImport
