import React from 'react'
import { Checkbox } from '@/components/checkBox'
import { Radio } from '@/components/radio/index'
import '../TabAndriod/index.scss'
import plusIcon from '../../../../../../assets/images/add.svg'
import { SmallHeading } from '@/components/headings/index'

class TabWeb extends React.Component {
    state = {
        iconImage: false,
        imageUrl: false,
        image: false,
        notificationSound: false,
        additionalPairs: [
            {
                key: '',
                value: '',
                canAddMore: true,
            },
        ],
    }

    _addPairs = () => {
        const { additionalPairs } = this.state
        additionalPairs.push({
            key: '',
            value: '',
            canAddMore: false,
        })

        this.setState({
            additionalPairs: additionalPairs.map((part, index) => {
                if (index === additionalPairs.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }

    _handlePairsInput = (index, key, value) => {
        const { additionalPairs } = this.state
        additionalPairs[index][key] = value
        this.setState({ additionalPairs })
    }

    _removePairs = index => {
        const { additionalPairs } = this.state
        additionalPairs.splice(index, 1)
        this.setState({ additionalPairs })
    }

    render() {
        const { iconImageUrl, imageUrl, iconImage, image } = this.state

        return (
            <div className="section section-grid">
                <div className="advance-row">
                    <Checkbox
                        checked={iconImage}
                        onChange={() => this.setState({ iconImage: !iconImage })}
                    >
                        Icon Image
                    </Checkbox>
                    {iconImage && (
                        <div className="mrgn-top-15 margn-left-15 ">
                            {/* <Radio checked="true">Upload Image</Radio> */}
                            <Radio
                                checked={iconImageUrl}
                                onClick={() => this.setState({ iconImageUrl: !iconImageUrl })}
                            >
                                Default Icon
                            </Radio>
                        </div>
                    )}
                </div>

                <div className="advance-row">
                    <Checkbox checked={image} onChange={() => this.setState({ image: !image })}>
                        Image
                    </Checkbox>
                    {image && (
                        <div className="mrgn-top-15 margn-left-15 ">
                            {/* <Radio checked="true">Upload Image</Radio> */}
                            <Radio
                                checked={imageUrl}
                                onClick={() => this.setState({ imageUrl: !imageUrl })}
                            >
                                Enter URL
                            </Radio>
                            {imageUrl && (
                                <div className="mrgn-top-15 row-child">
                                    <input
                                        type="text"
                                        className="form-input"
                                        placeholder="Enter URL"
                                    />
                                    <h6 className="input-footer">File supported: .mp3 and .m4a </h6>
                                </div>
                            )}
                        </div>
                    )}
                </div>

                <div className="divider-line" />
                <div>
                    <SmallHeading>Actions *</SmallHeading>
                    <h6>
                        Choose what happens when user clicks the body of this push notification.
                    </h6>
                    <div className="flex">
                        <input className="form-input mrgn-10" placeholder="Enter Deeplinking URL" />
                    </div>
                    <h5>Additional Key Value pairs (Optional):</h5>
                    {this.state.additionalPairs.map((part, index) => (
                        <div key={index} className="flex-space-bt">
                            <input
                                value={part.key}
                                onChange={e =>
                                    this._handlePairsInput(index, 'key', e.currentTarget.value)
                                }
                                className="form-input mrgn-10"
                                placeholder="key"
                            />
                            <input
                                className="form-input mrgn-10"
                                value={part.value}
                                onChange={e =>
                                    this._handlePairsInput(index, 'value', e.currentTarget.value)
                                }
                                placeholder="value"
                            />
                            {part.canAddMore ? (
                                <div className="plus" onClick={this._addPairs}>
                                    <img src={plusIcon} alt="" />
                                </div>
                            ) : (
                                <div className="minus" onClick={() => this._removePairs(index)}>
                                    -
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        )
    }
}

export default TabWeb
