import React, { Component } from 'react'
import WrapComponents from '@/components/wrapper'
import { Radio } from '@/components/radio'
import './index.scss'

class Segementation extends Component {
    state = {
        createSegment: true,
    }
    render() {
        const { createSegment } = this.state
        return (
            <WrapComponents>
                <div className="section">
                    <Radio
                        checked={!createSegment}
                        onClick={() => this.setState({ createSegment: !createSegment })}
                    >
                        All User
                    </Radio>
                    <Radio
                        checked={createSegment}
                        onClick={() => this.setState({ createSegment: !createSegment })}
                    >
                        Create Segment
                    </Radio>
                    {createSegment && (
                        <>
                            <div className="s-btn">
                                Include <b style={{ fontWeight: 'bolder' }}>+</b>
                            </div>
                            <Radio>Custom Segment</Radio>
                            <div className="s-btn">
                                Exclude <b style={{ fontWeight: 'bolder' }}>-</b>
                            </div>
                        </>
                    )}
                </div>
            </WrapComponents>
        )
    }
}

export default Segementation
