import React, { Component } from 'react'
import _ from 'lodash'
import history from '@/utils/history'
import Stepper from '@/components/stepper'
import ConnectComponent from '../../../../connect'
import WrapComponents from '@/components/wrapper'
import Alert from '@/components/alert'
import Loader from '@/components/loader'
import { SmallHeading } from '@/components/headings'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { GetCampaign, SaveStep2 } from '@/actions/CreateCampaign/emailCampaign'
import './index.scss'

class StepTwo extends Component {
    state = {
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview'],
        message: '',
        messageError: false,
        // clickURL: false,
        // clickURLvalue: '',
        campaignId: '',
        emoji: '',
        value: '',
    }

    async componentWillMount() {
        const {
            params: { campaignId },
        } = this.props.match
        this.setState({
            campaignId,
        })
        const res = await GetCampaign(campaignId)
        this.setState({
            message: _.get(res, 'message', ''),
        })
    }

    handleInputFields(key, value) {
        var currentText = e.target.value
        //Now we need to recalculate the number of characters that have been typed in so far
        var characterCount = currentText.length
        var charsPerPageCount = this.state.charsPerPage
        var unitCount = Math.round(characterCount / charsPerPageCount)
        this.setState({ pageCount: unitCount })
        this.setState({
            [key]: value,
        })
    }

    handleContinue = () => {
        const { message } = this.state
        let anyError = false
        if (message === '') {
            this.setState({
                messageError: true,
            })
            anyError = true
        }
        if (!anyError) {
            const { steps, campaignId, messageError, emoji, ...rest } = this.state
            const { data } = this.props.CreateCampaign
            SaveStep2({ ..._.assign({}, data, rest) })
            history.push(`/sms/step-3/${campaignId}`)
        }
    }

    render() {
        const { steps, message, messageError, emoji, value } = this.state
        const { error, loading } = this.props.CreateCampaign
        if (loading) {
            return <Loader />
        }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={1} />
                <div className="section">
                    <SmallHeading>Create Message</SmallHeading>
                    {messageError && <Alert className="alert">*This field is Required</Alert>}
                    {error && <Alert className="alert">*This field is Required</Alert>}
                    <textarea
                        multiline={true}
                        numberOfLines={6}
                        maxLength={130}
                        placeholder="I am awesome because..."
                        value={this.state.value}
                        onChange={value => this.setState({ value })}
                    />
                    <span>Characters Left: {this.state.value.length}/130</span>
                    <textarea
                        placeholder="Write your message here( *less than equal to 1000 words)"
                        name="message"
                        className="text-area"
                        value={message}
                        onChange={e => this.handleInputFields('message', e.target.value)}
                    />
                    {/* <div className="dotted-line" /> */}
                    {/* <Checkbox onClick={() => this.setState({ clickURL: !clickURL })}>
                        Track SMS Click for URL
                    </Checkbox>
                    {clickURL && (
                        <input
                            className="form-input mrgn-top-15"
                            placeholder="Paste/Type the URL you want to track"
                            name="clickURLvalue"
                            value={clickURLvalue}
                            onChange={e => this.handleInputFields('clickURLvalue', e.target.value)}
                        />
                    )} */}
                </div>

                <ColoredButtons onClick={this.handleContinue} className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons
                    onClick={() => this.props.history.goBack()}
                    className="float grey-border"
                >
                    Go Back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepTwo)
