import React, { Component } from 'react'
import Sidebar from '../../../components/sidebar'
import Header from '../../../components/navbar'
import WrapComponent from '@/components/wrapper'
import './index.scss'
import CampaignCard from '../components/campaignCards/index'

class CreateCampaign extends Component {
    render() {
        const { sidebarOpen } = this.state
        return (
            <WrapComponent>
                <CampaignCard/>
            </WrapComponent>
        )
    }
}

export default CreateCampaign

const Content = props => {
    var contentClass = props.isOpen ? 'dashboard-content open' : 'dashboard-content close'
    return <div className={contentClass}>{props.children}</div>
}
