import React, { Component } from 'react'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { Radio } from '@/components/radio'
import { ColoredButtons, TransparentButtons } from '@/components/button/index'

class StepFive extends Component {
    state = {
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
    }
    handleContinue = () => {
        // const { subject, sender, replyTO, from } = this.state

        let anyError = false

        // if (sender === '') {
        //     this.setState({
        //         senderError: true,
        //     })
        //     anyError = true
        // }
        if (!anyError) {
            this.props.onSubmit({})
        }
    }

    render() {
        const { steps } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={4} />
                <div className="section">
                    <div className="flex">
                        <div>
                            <Radio checked="true">Campaign Details</Radio>
                        </div>
                        <div>Edit</div>
                    </div>
                    <div className="dotted-line" />
                    <div className="row">
                        <div className="col-2">
                            <div>Campaign Name</div>
                        </div>
                        <div className="col-10">
                            <div>: value</div>
                        </div>
                    </div>
                </div>

                <div className="section">
                    <Radio checked="true">Scheduling</Radio>
                    <div className="dotted-line" />
                </div>
                <div className="section">
                    <Radio checked="true">Segmentation</Radio>
                    <div className="dotted-line" />
                </div>
                <div className="section">
                    <Radio checked="true">Send Test Notification</Radio>
                    <div className="dotted-line" />
                </div>
                <ColoredButtons onClick={this.handleContinue} className="float">
                    Send
                </ColoredButtons>
                <TransparentButtons className="float grey-border">Save as Draft</TransparentButtons>
            </WrapComponents>
        )
    }
}

export default StepFive
