import React, { Component } from 'react'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { Radio } from '@/components/radio'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings/index'
import ConnectComponent from '../../../../connect'

class StepOne extends Component {
    state = {
        source: 'toko-cm',
        type: 'General',
        campaignType: 'general',
        channel: 'Digital',
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        broadcast: true,
        name: '',
        nameError: false,
    }

    handleInputFields(key, value) {
        this.setState({
            [key]: value,
        })
        const { source, type, campaignType, channel, name } = this.state
        console.log(this.state)
        CreateCampaignAction({ source, type, campaignType, channel, name })
    }

    onSubmit = () => {
        let anyError = false
        if (!anyError) {
            this.props.onSubmit({
                source,
                type,
                campaignType,
                channel,
                name,
            })
        }
    }
    render() {
        const { steps, name, nameError, broadcast } = this.state
        console.log(this.props.data, 'CreateCampaignData')
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={0} />
                <div className="mrgn-btm">
                    <span>Channel :</span>
                    <span style={{ color: '#6cae53' }}>Email</span>
                </div>

                <div className="section">
                    <SmallHeading>Campaign Name</SmallHeading>
                    {nameError && <div className="alert">*This field is required</div>}
                    <input
                        placeholder="Enter Campaign Name"
                        type="text"
                        className="form-input"
                        name="name"
                        value={name}
                        onChange={e => this.handleInputFields('name', e.target.value)}
                    />
                </div>
                <div className="section">
                    <SmallHeading>Campaign Type*</SmallHeading>
                    <Radio checked={broadcast}>Broadcast Campaign</Radio>
                    {/* not active this functionality */}
                    {/* <Radio
                        checked={trigger}
                        onClick={() => this.setState({ trigger: !trigger, broadcast: !broadcast })}
                    >
                        Trigger Campaign
                    </Radio> */}
                </div>
                <ColoredButtons
                    as="link"
                    to="/email/step-2"
                    onClick={() => this.onSubmit}
                    className="float"
                >
                    Continue
                </ColoredButtons>
                <TransparentButtons
                    onClick={() => this.props.history.goBack()}
                    className="float grey-border"
                >
                    Go Back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepOne)
