import React, { Component } from 'react'
import WrapComponents from '@/components/wrapper'
import { Radio } from '@/components/radio'
import CreateRuleImport from './CreateRule'
import './index.scss'
import { SmallHeading } from '@/components/headings/index'

class CreateSegementation extends Component {
    state = {
        createSegment: true,
        inCludeType: 'createRule',
        exCludeType: 'createRule',
    }
    render() {
        const { createSegment, inCludeType, exCludeType } = this.state
        return (
            <div className="mrgn-top-15">
                <div className="section">
                    <SmallHeading>Create Segment</SmallHeading>
                    {createSegment && (
                        <>
                            <div className="s-btn mrgn-top-15">
                                Include <b style={{ fontWeight: 'bolder' }}>+</b>
                            </div>
                            <Radio
                                checked={inCludeType === 'customSegment' ? true : false}
                                onChange={() => this.setState({ inCludeType: 'customSegment' })}
                            >
                                Custom Segment
                            </Radio>
                            <Radio
                                checked={inCludeType === 'uploadCSV' ? true : false}
                                onChange={() => this.setState({ inCludeType: 'uploadCSV' })}
                            >
                                CSV Upload
                            </Radio>
                            <Radio
                                checked={inCludeType === 'createRule' ? true : false}
                                onChange={() => this.setState({ inCludeType: 'createRule' })}
                            >
                                Create Rule
                            </Radio>
                            {/* <hr className="mrgn-top-bottom-30" /> */}

                            {(() => {
                                switch (this.state.inCludeType) {
                                    case 'createRule':
                                        return <CreateRuleImport />
                                    case 'uploadCSV':
                                        return <div className="section">gruploadCSV</div>
                                    default:
                                        return <div className="section">createRule</div>
                                }
                            })()}
                            <div className="dotted-line mrgn-top-bottom-30" />

                            <>
                                <div className="s-btn">
                                    Exclude <b style={{ fontWeight: 'bolder' }}>-</b>
                                </div>
                                <Radio
                                    checked={exCludeType === 'customSegment' ? true : false}
                                    onChange={() => this.setState({ exCludeType: 'customSegment' })}
                                >
                                    Custom Segment
                                </Radio>
                                <Radio
                                    checked={exCludeType === 'customSegment' ? true : false}
                                    onChange={() => this.setState({ exCludeType: 'uploadCSV' })}
                                >
                                    CSV Upload
                                </Radio>
                                <Radio
                                    checked={exCludeType === 'createRule' ? true : false}
                                    onChange={() => this.setState({ exCludeType: 'createRule' })}
                                >
                                    Create Rule
                                </Radio>
                                {(() => {
                                    switch (exCludeType) {
                                        case 'createRule':
                                            return <CreateRuleImport />
                                        case 'uploadCSV':
                                            return <div className="section">gruploadCSV</div>
                                        default:
                                            return <div className="section">createRule</div>
                                    }
                                })()}
                            </>
                        </>
                    )}
                </div>
            </div>
        )
    }
}

export default CreateSegementation
