export const ChartConfig = {
    // colors: CHART_COLORS,
    credits: {
        enabled: false,
    },

    xAxis: {
        categories: [
            '1Dec',
            '2Dec',
            '3Dec',
            '4Dec',
            '5Dec',
            '6Dec',
            '7Dec',
            '8Dec',
            '9Dec',
            '10Dec',
        ],
        color: '#7b8891',
    },
    chart: {
        height: 150,
        width: 270,
        type: 'area',
    },

    title: {
        text: 'Past Data',
    },

    subtitle: {
        text: '',
    },

    yAxis: {
        title: {
            text: 'Users',
        },
    },
    legend: {
        layout: 'vertical',
        align: 'right',
        enabled: false,
        verticalAlign: 'middle',
    },
    tooltip: {
        backgroundColor: '#f8faff',
        borderColor: '#f8faff',
        borderRadius: 10,
        borderWidth: 3,
    },

    plotOptions: {
        areaspline: {
            fillOpacity: 0.1,
        },
    },

    series: [
        {
            name: 'Positivity',
            color: 'teal',
            data: [12, 14, 23, 34, 45, 56, 65, 43, 51, 41],
        },
    ],

    responsive: {
        rules: [
            {
                condition: {
                    maxWidth: 50,
                },
                chartOptions: {
                    legend: {
                        layout: 'horizontal',
                        align: 'center',
                        verticalAlign: 'bottom',
                    },
                },
            },
        ],
    },
}
