import React, { Component } from 'react'
import './index.scss'

export const Switch = ({ label, children, ...rest }) => {
    if (!id) {
        id = guidGenerator()
    }

    return (
        <div className="mrgn-btm">
            <input {...rest} type="checkbox" id="id-name--1" name="set-name" class="switch-input" />
            <label htmlFor="id-name--1" className="switch-label">
                <h5 className="label">{label}</h5>
                {children}
            </label>
        </div>
    )
}
