import React, { Component } from 'react'
import _ from 'lodash'
import connectComponent from '@/connect'
import WrapComponents from '@/components/wrapper'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import Loader from '@/components/loader'
import Alert from '@/components/alert'
import { getParentDetails } from '@/actions/CreateCampaign/PnCampaign'
import { UpdateCampaignBulk } from '@/actions/CreateCampaign/PnCampaign'
import { UploadContent } from '@/actions/utils'
import Segementation from '../Segmentation/index'
import './index.scss'

class StepFour extends Component {
    state = {
        parentID: '',
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview'],
        uploadCsv: true,
        createSegment: false,
        fileUploaded: false,
        error: false,
        targetUsers: 0,
        userFilePath: '',
        errorMessage: '',
    }

    componentWillMount = () => {
        let {
            params: { parent_id },
        } = this.props.match
        parent_id = parseInt(parent_id, 10)
        this.setState({
            parentID: parent_id,
        })
        getParentDetails(parent_id)
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    handleFileChange = async e => {
        const { files } = e.target
        if (files.length) {
            const res = await UploadContent(files[0], 'user_segment')
            if (res) {
                console.log(res, 'response in uploading the csv')
                this.setState({
                    fileUploaded: true,
                    targetUsers: res.targetUsers,
                    userFilePath: res.s3url,
                })
            }
        }
    }

    handleContinue() {
        const { data } = this.props.CreateCampaign
        const { fileUploaded, parentID, userFilePath, targetUsers } = this.state
        let anyError = false,
            noUploaded = '* Please upload user file containing user information',
            emptyFile = '*Error: file is empty. please upload another file.',
            uploadedFile = errorText => {
                this.setState({
                    error: true,
                })
                anyError = true
                return errorText
            }
        this.setState({
            errorMessage: fileUploaded
                ? !targetUsers
                    ? uploadedFile(emptyFile)
                    : null
                : uploadedFile(noUploaded),
            // errorMessage: fileUploaded ? null : uploadedFile(noUploaded),
        })
        if (!anyError) {
            const requests = []
            const next = `/push-notification/step-5/${parentID}`
            if (_.isArray(data)) {
                _.forEach(data, part => {
                    requests.push({
                        ..._.assign({}, part, { userFilePath }),
                    })
                })
            }
            UpdateCampaignBulk(requests, next)

            // history.push(`/push-notification/step-5/${parentID}`)
        }
    }

    render() {
        const {
            steps,
            uploadCsv,
            fileUploaded,
            error,
            targetUsers,
            errorMessage,
            createSegment,
        } = this.state
        const { CreateCampaign } = this.props
        if (CreateCampaign.loading) {
            return <Loader />
        }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={3} />
                {error && <Alert>{errorMessage}</Alert>}
                {CreateCampaign.error && <Alert>Error: {CreateCampaign.error}</Alert>}
                <div>
                    <div className="section-left">
                        <div className="section ">
                            <SmallHeading>Target Audience</SmallHeading>
                            <Radio
                                checked={uploadCsv}
                                onChange={() =>
                                    this.setState({
                                        uploadCsv: !uploadCsv,
                                        createSegment: !createSegment,
                                    })
                                }
                            >
                                Upload CSV
                            </Radio>
                            <Radio
                                checked={createSegment}
                                onChange={() =>
                                    this.setState({
                                        createSegment: !createSegment,
                                        uploadCsv: !uploadCsv,
                                    })
                                }
                            >
                                Create Segment
                            </Radio>
                            {createSegment && <Segementation />}
                            {uploadCsv && (
                                <div className="section mrgn-top-15">
                                    <div className="upload-file-section">
                                        <SmallHeading>Upload CSV file</SmallHeading>
                                        <label htmlFor="uploadFile">
                                            <input
                                                type="file"
                                                id="uploadFile"
                                                onChange={this.handleFileChange}
                                                accept=".csv"
                                                className="upload-file"
                                            />
                                        </label>
                                    </div>
                                    {fileUploaded && (
                                        <>
                                            <Alert className="success mrgn-top-15">
                                                File has been uploaded successfully
                                            </Alert>
                                            <SmallHeading className="success mrgn-top-15">
                                                Number of users in files : {targetUsers}
                                            </SmallHeading>
                                        </>
                                    )}
                                </div>
                            )}
                        </div>
                        <ColoredButtons
                            className="float"
                            onClick={() => {
                                this.handleContinue()
                            }}
                        >
                            Continue
                        </ColoredButtons>
                        <TransparentButtons
                            onClick={() => this.props.history.goBack()}
                            className="float grey-border"
                        >
                            Go Back
                        </TransparentButtons>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default connectComponent(StepFour)
