import React from 'react';
import { Router, Route, Switch } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import _ from 'lodash';
import history from '@/utils/history';

// Import all pages
import Home from './Home';
import Login from './Login';
import Dashboard from './Dashboard'

const urlPatterns = [
    {
        path: '/',
        component: Home,
        title: 'Home',
    },
    {
        path: '/dashboard',
        component: Dashboard,
        title: 'Dashboard',
    },
    {
        path: '/login',
        component: Login,
        title: 'Login',
    },
];

// Bind pages to urls
const RootRouter = () => (
    <Router history={history}>
        <Switch>
            {_.map(urlPatterns, (url, index) => (
                <Route
                    key={index}
                    path={url.path}
                    exact
                    render={route => (
                        <>
                            <Helmet>
                                <title>{url.title || 'Home'}</title>
                            </Helmet>
                            <url.component {...route} />
                        </>
                    )}
                />
            ))}
            <Route component={() => <div className="pageNotFound">404 | page not found</div>} />
        </Switch>
    </Router>
);

export default RootRouter;
