import React from 'react'
import _ from 'lodash'

const Stepper = ({ steps, activeStep }) => {
    if (!steps) {
        return null
    }

    const lastIndex = steps.length - 1;

    return (
        <div className="stepper">
            {_.map(steps, (part, index) => {
                if (index === activeStep) {
                    return part
                }
                return part
            })}
        </div>
    )
}

export default Stepper
