import React, { Component } from 'react'
// import Header from '../../components/header';
// import Sidebar from '../../components/sidebar';
// import Container from '../../components/container';
// import { ColoredButtons } from "../../components/buttons/"
// // actions
// import { fetchCompaigns, showMessage } from '../../store/actions'
import ConnectComponent from '../../../connect'

// // components
// import ListView from './components/ListView'
// import Alert from '../../components/alert'

class Compaign extends Component {
    componentWillMount() {
        fetchCompaigns()
    }

    render() {
        const { campaignsList, campaignsHeader, loading, messages } = this.props.campaign
        return (
            <div>
                <Header />
                <Sidebar />
                <Container className="main-content">
                    <div className="d-flex align-items-center">
                        <div className="header-left">Campaigns</div>
                        <div className="header-right ml-auto">
                            <div style={{ position: 'relative' }}>
                                <ColoredButtons className="pri" as="link" to="">
                                    Force Stop All
                                </ColoredButtons>
                                <ColoredButtons className="pri" as="link" to="/campaign/new/step1">
                                    Create Campaign
                                </ColoredButtons>
                            </div>
                        </div>
                    </div>
                    <div className="line" />
                    {messages && (
                        <Alert className="success" onClose={() => showMessage('')}>
                            {messages}
                        </Alert>
                    )}
                    <ListView data={campaignsList} headers={campaignsHeader} loading={loading} />
                </Container>
            </div>
        )
    }
}

export default ConnectComponent(Compaign)
