import React, { Component } from 'react'
import WrapComponents from '@/components/wrapper'
import { LargeHeading } from '@/components/headings'
import './index.scss'
// import Switch from '@/components/Switch'

class Settings extends Component {
    state = {
        toggleBar: false,
    }
    render() {
        const { toggleBar } = this.state
        console.log(toggleBar, 'toggleBar')
        return (
            <WrapComponents>
                <LargeHeading>Push Notification Settings</LargeHeading>
                <h5>Setting Up Push Notification</h5>
                <div className="divider-line" />
                <div className="tab-bar row">
                    <div
                        onClick={() => {
                            this.setState({ toggleBar: 'one' })
                        }}
                        className={`bar col active $ {toggleBar==='one' ? 'active' : ''}`}
                    >
                        Mobile Push
                    </div>
                    <div
                        onClick={() => {
                            this.setState({ toggleBar: 'two' })
                        }}
                        className={`bar col active $ {toggleBar==='one' ? 'active' : ''}`}
                    >
                        FC & Do Not Disturb
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default Settings
