import React, { Component } from 'react'
import AceEditor from 'react-ace'
import './index.scss'
import HtmlPreview from './htmlpreview'
import ConnectComponent from '../../../../connect'

class CodeCompaign extends Component {
    editor = null
    state = {
        emailHtml:
            "<!DOCTYPE html><html><head><title></title><meta name='viewport' content='width=device-width, initial-scale=1.0'><style type='text/css'>@media only screen and (max-width: 400px) !important{.iconsbit div img{width:15px !important;} }</style></head><body><div class='followus'><p style='font-size: 14px; font-family: impact; color: #2d2d52; text-align: center; margin:auto;'>FOLLOW US</p></div><div class ='iconsbit' style='border-radius: 5px;display: flex;margin:auto; width: 165px; max-width: 225px;'><div style='padding: 7px 5px; margin-right: 0px;'><a href='https://www.facebook.com/bitnautic/?ref=br_rs' target='_blank'><img src='https://image.ibb.co/f4oo5T/Facebook_icon.png' style='width:18px;'></a></div><div style='padding: 7px 5px; margin-right: 0px;'> <a href='https://www.instagram.com/bitnautic/' target='_blank'><img src='https://image.ibb.co/n6wVKo/instagram_logo.png' style='width:18px;'></a></div><div style='padding: 7px 5px; margin-right: 0px;'> <a href='https://www.linkedin.com/company/bitnautic-official/' target='_blank'><img src='https://image.ibb.co/ettAKo/linkedin_logo_1.png' style='width:18px;'></a></div><div style='padding: 7px 5px; margin-right: 0px;'> <a href='https://bitcointalk.org/index.php?topic=3351552.0' target='_blank'><img src='https://image.ibb.co/cFpvkT/Bitcointalk.png' style='width:18px;'></a></div><div style='padding: 7px 5px; margin-right: 0px;'> <a href=' https://t.me/bitnautic_group' target='_blank'><img src='https://image.ibb.co/dOZvkT/telegram1.png' style='width:18px; margin-right: 0px;'></a></div><div style='padding: 7px 4px; margin-left:0px; margin-right: auto;'> <a href='https://www.reddit.com/r/bitnautic' target='_blank'><img src='https://image.ibb.co/j2JzC8/reddit_social_logo_character.png' style='width:18px;' ></a></div></div><div class='bottom-text'> <p style='font-size:7px;color: black;top: 45%;text-align: center;'> You are receiving this email because you subscribed to the newsletter on the website <a href='https://bitnautic.io/?pk_campaign=Bitnautic_Emailer_28_06_Growth_Potential&pk_medium=sendgrid' target='_blank' >www.bitnautic.io.</a>You can <a href='<%asm_preferences_raw_url%>'>update your preferences</a> or <a href='[unsubscribe]'>unsubscribe from this list</a>.</p></div></div></body></html> ",
    }

    handleAceInput = value => {
        this.setState({
            emailHtml: value,
        })
    }

    handleInputChange = (key, value) => {
        this.setState({
            [key]: value,
        })
    }

    handleSenderChange = sender => {
        this.setState({
            selectedSender: sender,
        })
    }

    handleRecepientChange = r => {
        this.setState({
            selectedSendTo: r,
        })
    }

    handleUSGChange = r => {
        this.setState({
            selectedUsg: r,
        })
    }

    handleDateChange = date => {
        this.setState({
            startDate: date,
        })
    }

    handleTimeChange = time => {
        this.setState({
            startTime: time,
        })
    }

    switchPreview = preview => {
        this.setState({
            currentPreview: preview,
        })
    }

    handleDripLevelChange = value => {
        this.setState({
            dripLevelSelected: value,
        })

        if (this.state.subject !== '') {
            const params = {
                html_content: this.state.emailHtml,
                subject: this.state.subject,
                custom_unsubscribe_url: this.state.customULink,
                campaign_id: this.state.campaignId,
                email_id: this.state.dripLevelSelected,
            }
            // updateCampaignEmailDrip(params)
            params.email_id = value
            // getCampaignEmailDrip(params)
            this.setState({
                subject: '',
                customULink: '',
                emailHtml:
                    "<!DOCTYPE html><html><head><title></title><meta name='viewport' content='width=device-width, initial-scale=1.0'><style type='text/css'>@media only screen and (max-width: 400px) !important{.iconsbit div img{width:15px !important;} }</style></head><body><div class='followus'><p style='font-size: 14px; font-family: impact; color: #2d2d52; text-align: center; margin:auto;'>FOLLOW US</p></div><div class ='iconsbit' style='border-radius: 5px;display: flex;margin:auto; width: 165px; max-width: 225px;'><div style='padding: 7px 5px; margin-right: 0px;'><a href='https://www.facebook.com/bitnautic/?ref=br_rs' target='_blank'><img src='https://image.ibb.co/f4oo5T/Facebook_icon.png' style='width:18px;'></a></div><div style='padding: 7px 5px; margin-right: 0px;'> <a href='https://www.instagram.com/bitnautic/' target='_blank'><img src='https://image.ibb.co/n6wVKo/instagram_logo.png' style='width:18px;'></a></div><div style='padding: 7px 5px; margin-right: 0px;'> <a href='https://www.linkedin.com/company/bitnautic-official/' target='_blank'><img src='https://image.ibb.co/ettAKo/linkedin_logo_1.png' style='width:18px;'></a></div><div style='padding: 7px 5px; margin-right: 0px;'> <a href='https://bitcointalk.org/index.php?topic=3351552.0' target='_blank'><img src='https://image.ibb.co/cFpvkT/Bitcointalk.png' style='width:18px;'></a></div><div style='padding: 7px 5px; margin-right: 0px;'> <a href=' https://t.me/bitnautic_group' target='_blank'><img src='https://image.ibb.co/dOZvkT/telegram1.png' style='width:18px; margin-right: 0px;'></a></div><div style='padding: 7px 4px; margin-left:0px; margin-right: auto;'> <a href='https://www.reddit.com/r/bitnautic' target='_blank'><img src='https://image.ibb.co/j2JzC8/reddit_social_logo_character.png' style='width:18px;' ></a></div></div><div class='bottom-text'> <p style='font-size:7px;color: black;top: 45%;text-align: center;'> You are receiving this email because you subscribed to the newsletter on the website <a href='https://bitnautic.io/?pk_campaign=Bitnautic_Emailer_28_06_Growth_Potential&pk_medium=sendgrid' target='_blank' >www.bitnautic.io.</a>You can <a href='<%asm_preferences_raw_url%>'>update your preferences</a> or <a href='[unsubscribe]'>unsubscribe from this list</a>.</p></div></div></body></html> ",
            })
        }
    }

    render() {
        return (
            <div className="u-email-editor">
                <div className="flex">
                    <div className="section margn-right-15 code-editor">
                        <AceEditor
                            width="100%"
                            height="100%"
                            mode="html"
                            theme="solarized_dark"
                            value={this.state.emailHtml}
                            onChange={this.handleAceInput}
                        />
                    </div>
                    <div className="section code-preview">
                        <div className="code-preivew-header">
                            <div
                                className="preview-icons web"
                                onClick={() => this.switchPreview(0)}
                            />
                            <div
                                className="preview-icons mobile"
                                onClick={() => this.switchPreview(1)}
                            />
                        </div>
                        <div className="code-preivew-body">
                            {this.state.currentPreview === 0 && (
                                <HtmlPreview src={this.state.emailHtml} />
                            )}
                            {this.state.currentPreview === 1 && (
                                <div className="preview-mobile">
                                    <div className="mobile-speaker" />
                                    <div className="mobile-camera" />
                                    <div className="mobile-preview-content">
                                        <HtmlPreview src={this.state.emailHtml} />
                                    </div>
                                    <div className="mobile-button" />
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default ConnectComponent(CodeCompaign)
