import React, { Component } from 'react'
import _ from 'lodash'
import connectComponent from '@/connect'
import WrapComponents from '@/components/wrapper'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import Loader from '@/components/loader'
import Alert from '@/components/alert'
import history from '@/utils/history'
import { getParentDetails } from '@/actions/CreateCampaign/PnCampaign'
// import { UpdateCampaignBulk } from '@/actions/CreateCampaign/PnCampaign'
import { UploadContent, AddUser } from '@/actions/utils'
import './index.scss'

class StepFour extends Component {
    state = {
        parentID: '',
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview'],
        uploadCsv: true,
        fileUploaded: false,
        error: false,
        usersCount: 0,
    }

    componentWillMount = () => {
        let {
            params: { parent_id },
        } = this.props.match
        parent_id = parseInt(parent_id, 10)
        this.setState({
            parentID: parent_id,
        })
        if (!this.props.CreateCampaign.data) {
            getParentDetails(parent_id)
        }
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    handleFileChange = async e => {
        const { files } = e.target
        if (files.length) {
            const res = await UploadContent(files[0], 'user_segment')
            if (res) {
                this.setState({
                    fileUploaded: true,
                    usersCount: res.usersCount,
                    userFilePath: response.s3url,
                })
                const { data } = this.props.CreateCampaign
                const requests = []
                const next = `/push-notification/step-4/${parentID}`
                if (_.isArray(data)) {
                    _.forEach(data, part => {
                        requests.push({
                            ...params,
                            campaignId: part.campaignId || part.campaignId,
                        })
                    })
                }
                UpdateCampaignBulk(requests, next)

                // if (_.isArray(data)) {
                //     _.forEach(data, part => {
                //         requests.push(
                //             AddUser({
                //                 segment_id: res.id,
                //                 campaignId: part.campaignId || part.campaignId,
                //             })
                //         )
                //     })
                // }
                // const adduser = await Promise.all(requests)
                // console.log('adduser in pn', adduser[0])
            }
        }
    }

    handleContinue() {
        let anyError = false
        const { fileUploaded, parentID } = this.state
        if (!fileUploaded) {
            this.setState({
                error: true,
            })
            anyError = true
        }
        if (!anyError) {
            history.push(`/push-notification/step-5/${parentID}`)
        }
    }

    render() {
        const { steps, uploadCsv, fileUploaded, error, usersCount } = this.state
        const { CreateCampaign } = this.props
        if (CreateCampaign.loading) {
            return <Loader />
        }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={3} />
                {error && <Alert>Error: Please add users for Campaign</Alert>}
                {CreateCampaign.error && <Alert>Error: {CreateCampaign.error}</Alert>}
                <div>
                    <div className="section-left">
                        <div className="section ">
                            <SmallHeading>Target Audience</SmallHeading>
                            {/* <Radio>All users</Radio>
                            <Radio>Saved Segments</Radio> */}
                            <Radio checked={true}>Upload CSV</Radio>
                            {uploadCsv && (
                                <div className="section mrgn-top-15">
                                    <div className="upload-file-section">
                                        <SmallHeading>Upload CSV file</SmallHeading>
                                        <label htmlFor="uploadFile" className="upload-box">
                                            <input
                                                type="file"
                                                id="uploadFile"
                                                onChange={this.handleFileChange}
                                                accept=".csv"
                                                className="upload-file"
                                            />
                                        </label>
                                    </div>
                                    {fileUploaded && (
                                        <>
                                            <Alert className="success mrgn-top-15">
                                                File has been uploaded successfully
                                            </Alert>
                                            <SmallHeading className="success mrgn-top-15">
                                                Number of users in files : {usersCount}
                                            </SmallHeading>
                                        </>
                                    )}
                                </div>
                            )}
                        </div>
                        <ColoredButtons
                            className="float"
                            onClick={() => {
                                this.handleContinue()
                            }}
                        >
                            Continue
                        </ColoredButtons>
                        <TransparentButtons
                            onClick={() => this.props.history.goBack()}
                            className="float grey-border"
                        >
                            Go Back
                        </TransparentButtons>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default connectComponent(StepFour)
