import React from 'react'
import cx from 'classnames'
import './index.scss'

class Alert extends React.Component {
    timer = null
    state = {
        mount: true,
    }

    componentDidMount() {
        if (!this.timer) {
            this.timer = window.setTimeout(() => {
                this.setState({
                    mount: false,
                })
                if (this.props.onClose) {
                    this.props.onClose()
                }
            }, this.props.timeout || 10000)
        }
    }

    componentWillUnmount() {
        if (this.timer) {
            window.clearTimeout(this.timer)
        }
    }

    clearTimer = () => {
        if (this.timer) {
            window.clearTimeout(this.timer)
            if (this.props.onClose) {
                this.props.onClose()
            }
        }
    }

    render() {
        const { className, children } = this.props
        if (this.state.mount) {
            return (
                <div className={cx('alert', { [className]: className })}>
                    {children}
                    <div className="alert-close" onClick={this.clearTimer}>
                        &times;
                    </div>
                </div>
            )
        }
        return null
    }
}

export default Alert
