import React, { Component } from 'react'
import WrapComponent from '@/components/wrapper'
import TableExamplePagination from './campaignList'
import { campaignListData } from './dummyData'
import Select from 'react-select'

const headers = [
    { key: 'name', value: 'Campaign Name' },
    { key: 'campaignType', value: 'Type' },
    { key: 'channel', value: 'Channel' },
    { key: '', value: 'Actions' },
    { key: 'scheduledTime', value: 'Schedule Time' },
]

class CampaignManage extends Component {
    render() {
        return (
            <>
                <WrapComponent>
                    <div className="flex">
                        <Select
                            closeMenuOnSelect={false}
                            isMulti={false}
                            options={NotificationSoundOPtions}
                            theme={theme => ({
                                ...theme,
                                borderRadius: 0,
                                colors: {
                                    ...theme.colors,
                                    text: '#f1f1f1',
                                    primary25: '#74d69e',
                                    primary: '#74d69e',
                                },
                            })}
                            placeholder="Created Date"
                        />
                        <Select
                            closeMenuOnSelect={false}
                            isMulti={false}
                            options={NotificationSoundOPtions}
                            theme={theme => ({
                                ...theme,
                                borderRadius: 0,
                                colors: {
                                    ...theme.colors,
                                    text: '#f1f1f1',
                                    primary25: '#74d69e',
                                    primary: '#74d69e',
                                },
                            })}
                            placeholder="Type"
                        />
                        <Select
                            closeMenuOnSelect={false}
                            isMulti={false}
                            options={NotificationSoundOPtions}
                            theme={theme => ({
                                ...theme,
                                borderRadius: 0,
                                colors: {
                                    ...theme.colors,
                                    text: '#f1f1f1',
                                    primary25: '#74d69e',
                                    primary: '#74d69e',
                                },
                            })}
                            placeholder="Status"
                        />
                        <Select placeholder="Platform" />
                    </div>
                    <TableExamplePagination
                        loading={false}
                        data={campaignListData.campaigns}
                        headers={headers}
                    />
                </WrapComponent>
            </>
        )
    }
}

export default CampaignManage
