import React, { Component } from 'react'
import _ from 'lodash'
import history from '@/utils/history'
import ConnectComponent from '@/connect'
import Stepper from '@/components/stepper'
import { Radio } from '@/components/radio'
import { SmallHeading } from '@/components/headings'
import Alert from '@/components/alert'
import Loader from '@/components/loader'
import CreateSegementation from '../../../Segmentation/createSegment'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import WrapComponents from '@/components/wrapper'
import {
    UploadContent,
    GetCampaign,
    UpdateCampaignData,
} from '@/actions/CreateCampaign/emailCampaign'
import CSVIcon from '@/assets/images/csv-icon.svg'

class StepFour extends Component {
    state = {
        campaignId: '',
        userFilePath: '',
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        uploadCsv: true,
        createSegment: false,
        fileUploaded: false,
        targetTypeInCsv: '',
        error: false,
        targetUsers: 0,
        errorMessage: '',
        filePreview: null,
        loading: false,
    }

    async componentWillMount() {
        const {
            params: { campaignId },
        } = this.props.match
        this.setState({
            campaignId,
        })
        await GetCampaign(campaignId)
        this.getUserFilePath()
    }

    getUserFilePath = () => {
        const {
            CreateCampaign: { data },
        } = this.props
        this.setState({
            filePreview: this.getFileName(data),
        })
    }

    getFileName = campaign => {
        const userFilePath = _.get(campaign, 'userFilePath')
        const userFile = _.get(campaign, 'appVersion')

        if (userFilePath) {
            return {
                name: userFile,
                file: userFilePath,
            }
        }
        return null
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    handleFileChange = async e => {
        const { files } = e.target
        if (files.length) {
            const response = await UploadContent(files[0], 'user_segment')
            if (response) {
                this.setState({
                    fileUploaded: true,
                    userFilePath: response.s3url,
                    targetUsers: response.targetUsers,
                    targetTypeInCsv: response.targetTypeInCsv,
                    filePreview: {
                        name: files[0].name,
                    },
                })
            }
        }
    }

    handleContinue = () => {
        const {
            userFilePath,
            fileUploaded,
            campaignId,
            targetUsers,
            filePreview,
            createSegment,
            targetTypeInCsv,
        } = this.state
        let hasError = false

        if (!fileUploaded && filePreview) {
            return history.push(`/sms/step-5/${campaignId}`)
        }

        if (!targetUsers) {
            this.setState({
                error: true,
                errorMessage: 'Error: file is empty.',
            })
            hasError = true
        }
        if (createSegment) {
            this.setState({
                error: true,
                errorMessage: 'Please upload user file containing user information',
            })
            hasError = true
        }

        if (!fileUploaded) {
            this.setState({
                error: true,
                errorMessage: 'Please upload user file containing user information',
            })
            hasError = true
        }

        if (!hasError) {
            const { data } = this.props.CreateCampaign
            data.appVersion = filePreview.name
            UpdateCampaignData({
                ..._.assign({}, data, { userFilePath }),
            })
            history.push(`/sms/step-5/${campaignId}`)
        }
    }

    render() {
        const {
            steps,
            uploadCsv,
            fileUploaded,
            error,
            targetUsers,
            errorMessage,
            createSegment,
            filePreview,
            userFilePath,
        } = this.state
        const { CreateCampaign } = this.props

        if (CreateCampaign.loading) {
            return <Loader />
        }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={3} />
                {CreateCampaign.error && <Alert>Error: {CreateCampaign.error}</Alert>}
                <div>
                    <div className="section-left">
                        <div className="section ">
                            <SmallHeading>Target Audience</SmallHeading>
                            <Radio
                                checked={uploadCsv}
                                onChange={() =>
                                    this.setState({
                                        uploadCsv: !uploadCsv,
                                        createSegment: !createSegment,
                                    })
                                }
                            >
                                Upload CSV
                            </Radio>
                            <Radio
                                checked={createSegment}
                                onChange={() =>
                                    this.setState({
                                        createSegment: !createSegment,
                                        uploadCsv: !uploadCsv,
                                    })
                                }
                            >
                                Create Segment
                            </Radio>
                            {createSegment && <CreateSegementation />}

                            {uploadCsv && (
                                <div className="section mrgn-top-15">
                                    <div className="upload-file-section">
                                        <SmallHeading>Upload CSV file</SmallHeading>
                                        <label htmlFor="uploadFile" className="">
                                            <input
                                                type="file"
                                                id="uploadFile"
                                                onChange={this.handleFileChange}
                                                accept=".csv"
                                                className="upload-file"
                                            />
                                        </label>
                                    </div>
                                    {fileUploaded && (
                                        <>
                                            <Alert className="success mrgn-top-15">
                                                Number of users in files : {targetUsers}
                                            </Alert>
                                            <SmallHeading className="success mrgn-top-15">
                                                {targetUsers} Users added
                                            </SmallHeading>
                                        </>
                                    )}
                                    {filePreview && (
                                        <a
                                            className="file-uploaded"
                                            href={userFilePath}
                                            target="_blank"
                                            rel="noopener nofollow"
                                        >
                                            <img
                                                className="file-icon"
                                                src={CSVIcon}
                                                alt="csv icon"
                                            />
                                            <div className="file-name">{filePreview.name}</div>
                                        </a>
                                    )}
                                </div>
                            )}
                        </div>
                        {error && (
                            <Alert onClose={() => this.setState({ error: false })}>
                                {errorMessage}
                            </Alert>
                        )}

                        <ColoredButtons onClick={this.handleContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons
                            onClick={() => this.props.history.goBack()}
                            className="float grey-border"
                        >
                            Go Back
                        </TransparentButtons>
                    </div>
                    {CreateCampaign.error && <div className="alert">{CreateCampaign.error}</div>}
                </div>
            </WrapComponents>
        )
    }
}
export default ConnectComponent(StepFour)
