import React, { Component } from 'react'
import './index.scss'
import { SmallHeading } from '@/components/headings'
import Select from 'react-select'
import { LargeHeading } from '../headings/index'

export const NotificationSoundOPtions = [{ value: 'Default', label: 'Default' }]

class TriggerCampaign extends Component {
    state = {
        actionButtons: [
            {
                attribute: '',
                operator: '',
                value: '',
                canAddMore: true,
            },
        ],
    }

    _addMoreActions = () => {
        const { actionButtons } = this.state
        actionButtons.push({
            text: '',
            appLink: '',
            canAddMore: false,
            icon: '',
        })
        this.setState({
            actionButtons: actionButtons.map((part, index) => {
                if (index === actionButtons.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }

    render() {
        return (
            <>
                <SmallHeading>Primary Actions for Trigger Campaign</SmallHeading>
                <div className="dasshed-line" />
                <Select
                    closeMenuOnSelect={false}
                    isMulti={false}
                    options={NotificationSoundOPtions}
                    theme={theme => ({
                        ...theme,
                        borderRadius: 0,
                        colors: {
                            ...theme.colors,
                            text: '#f1f1f1',
                            primary25: '#74d69e',
                            primary: '#74d69e',
                        },
                    })}
                    placeholder="Select Event name"
                />
                <div className="flex filter mrgn-top-15">
                    <Select
                        closeMenuOnSelect={false}
                        isMulti={false}
                        options={NotificationSoundOPtions}
                        theme={theme => ({
                            ...theme,
                            borderRadius: 0,
                            colors: {
                                ...theme.colors,
                                text: '#f1f1f1',
                                primary25: '#74d69e',
                                primary: '#74d69e',
                            },
                        })}
                        placeholder="Select Attribute name"
                    />
                    <Select
                        closeMenuOnSelect={false}
                        isMulti={false}
                        options={NotificationSoundOPtions}
                        theme={theme => ({
                            ...theme,
                            borderRadius: 0,
                            colors: {
                                ...theme.colors,
                                text: '#f1f1f1',
                                primary25: '#74d69e',
                                primary: '#74d69e',
                            },
                        })}
                        placeholder="Select Operator name"
                    />
                    <Select
                        closeMenuOnSelect={false}
                        isMulti={false}
                        options={NotificationSoundOPtions}
                        theme={theme => ({
                            ...theme,
                            borderRadius: 0,
                            colors: {
                                ...theme.colors,
                                text: '#f1f1f1',
                                primary25: '#74d69e',
                                primary: '#74d69e',
                            },
                        })}
                        placeholder="Select Value"
                    />
                </div>
            </>
        )
    }
}

export default TriggerCampaign
