import React from 'react'
import { Router, Route, Switch } from 'react-router-dom'
import { Helmet } from 'react-helmet'
import _ from 'lodash'
import history from '@/utils/history'
import '../index.scss'

// Import all pages
import Login from './Login'
import SelectCampaignType from './Campaign'
import CreatePushNotifications from './PushNotification'
import CreateSMSCampaign from './SMSCampaign'
import CreatEmailCampaign from './EmailCampaign'
import Segementation from './Segmentation'
import Settings from './Settings'
import CreateInApp from './InAppCampaign'
import OverViewPage from './OverViewPage'
import CampaignManage from './Campaign/ManageCampaign'

const urlPatterns = [
    {
        path: '/',
        component: Login,
        title: 'Login',
        exact: true,
    },
    {
        path: '/over-view',
        component: OverViewPage,
        title: 'Over View',
        exact: true,
    },
    {
        path: '/home',
        component: CampaignManage,
        title: 'CampaignManage',
        exact: true,
    },

    {
        path: '/segmentation',
        component: Segementation,
        title: 'Segementation',
        exact: true,
    },

    {
        path: '/campaign',
        component: SelectCampaignType,
        title: 'Select Campaign',
        exact: true,
    },
    {
        path: '/settings',
        component: Settings,
        title: 'Settings',
        exact: true,
    },
    {
        path: '/push-notification',
        component: CreatePushNotifications,
        title: 'Push Notification',
        exact: false,
    },
    {
        path: '/email',
        component: CreatEmailCampaign,
        title: 'Email Campaign',
        exact: false,
    },
    {
        path: '/sms',
        component: CreateSMSCampaign,
        title: 'SMS Campaign',
        exact: false,
    },
    {
        path: '/in-app',
        component: CreateInApp,
        title: 'InApp Campaign',
        exact: false,
    },
]

const RootRouter = () => (
    <Router
        history={history}
        // basename="/imt-fe"
    >
        <Switch>
            {_.map(urlPatterns, (url, index) => (
                <Route
                    key={index}
                    path={url.path}
                    exact={url.exact}
                    render={route => (
                        <>
                            <Helmet>
                                <title>{url.title || 'Home'}</title>
                            </Helmet>
                            <url.component {...route} />
                        </>
                    )}
                />
            ))}
            <Route component={() => <div className="pageNotFound" />} />
        </Switch>
    </Router>
)

export default RootRouter
