import React, { Component } from 'react'
import WrapComponents from '@/components/wrapper'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import { Checkbox } from '@/components/checkBox'
import Stepper from '@/components/stepper'
import './index.scss'
import androidMobile from '../../../../assets/images/andriod.png'

class StepThree extends Component {
    state = {
        steps: ['Channel', 'Message', 'Segmentation', 'Scheduling', 'Overview'],
    }
    saveAndContinue = e => {
        e.preventDefault()
        this.props.data.push(this.state)
        this.props.nextStep()
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    render() {
        const { steps } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={1} />
                <div className="flex-space-bt ">
                    <div className="section-left">
                        <div className="section ">
                            <SmallHeading>Target Audience</SmallHeading>
                            <Radio>All users</Radio>
                            <Radio>Saved Segments</Radio>
                            <Radio>Upload CSV</Radio>
                            <div className="devider-line" />
                            <Checkbox
                            // onClick={e => {
                            //     this.toggleState(!advance)
                            // }}
                            >
                                Exclude Users
                            </Checkbox>
                        </div>
                        <div className="section">Show Count</div>
                        <ColoredButtons onClick={this.saveAndContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons onClick={this.back} className="float">
                            Save as Draft
                        </TransparentButtons>
                    </div>
                    <div className="section section-right">
                        <SmallHeading>Preview</SmallHeading>
                        <div className="preview-text">
                            Actual display may be different to this preview depending on the user’s
                            device.
                        </div>
                        <div className="preview">
                            <img src={androidMobile} className="preview-image" alt="" />
                            <div className="view-pn">
                                <div className="mobile-header">Sample App • Summary • Now</div>
                                <div className="content-view">
                                    <div>
                                        <div className="preview-tittle">Your message title</div>
                                        <div className="preview-msg">your message content</div>
                                    </div>
                                    <div className="pn-image" />
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default StepThree
