import axios from 'axios'
import * as Auth from '@/constants/Endpoints'

export default {
    loginUser: data => axios({ ...Auth.LOGIN, data }).then(res => res.data),
    createPN: data => axios({ ...Auth.CREATE_PN, data }).then(res => res.data),
    createParent: () => axios({ ...Auth.CAMPAIGN_PARENT_ID }).then(res => res.data),
    CreateCampaignAction: data => axios({ ...Auth.CREATE_CAMPAIGN, data }).then(res => res.data),
    CreateContent: data => axios({ ...Auth.CREATE_CONTENT, data }).then(res => res.data),
    GetCampaignAction: campaignID => {
        Auth.GET_CAMPAIGN_DETAILS.url = Auth.GET_CAMPAIGN_DETAILS.url.replace(
            '<campaignID>',
            campaignID
        )
        return axios({ ...Auth.GET_CAMPAIGN_DETAILS }).then(res => res.data)
    },
    addPNUser: data =>
        axios({
            ...Auth.ADD_PN_USERS,
            headers: {
                'X-GA-ID': '2377',
            },
            data,
        }).then(res => res.data),
    addCampaignUser: data => axios({ ...Auth.ADD_CAMPAIGN_USER, data }).then(res => res.data),
}
