import React, { Component } from 'react'
import Stepper from '@/components/stepper'
import { Radio } from '@/components/radio'
import { SmallHeading } from '@/components/headings'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import WrapComponents from '@/components/wrapper'

class StepFour extends Component {
    state = {
        steps: ['Channel', 'Message', 'Scheduling', 'Overview', 'send'],
        uploadCsv: false,
    }
    saveAndContinue = e => {
        e.preventDefault()
        this.props.nextStep()
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    handleFileChange = e => {
        const { files } = e.target
        if (files.length) {
            const formData = new FormData()
            formData.append('input_file', files[0])
            // uploadSegmentFileAction(formData)
        }
    }

    render() {
        const { steps, uploadCsv } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={2} />
                <div>
                    <div className="section-left">
                        <div className="section ">
                            <SmallHeading>Target Audience</SmallHeading>
                            <Radio
                                checked={uploadCsv}
                                onClick={() => this.setState({ uploadCsv: !uploadCsv })}
                            >
                                Upload CSV
                            </Radio>
                            {uploadCsv && (
                                <div className="section mrgn-top-15">
                                    <div className="upload-file-section">
                                        <SmallHeading>Upload CSV file</SmallHeading>
                                        <label htmlFor="uploadFile" className="upload-box">
                                            <input
                                                type="file"
                                                id="uploadFile"
                                                onChange={this.handleFileChange}
                                                accept=".csv"
                                                className="upload-file"
                                            />
                                        </label>
                                    </div>
                                </div>
                            )}
                        </div>
                        <ColoredButtons onClick={this.saveAndContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons onClick={this.back} className="float grey-border">
                            Save as Draft
                        </TransparentButtons>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}
export default StepFour
