import React, { Component } from 'react'
import _ from 'lodash'
import StepOne from './Components/steps/step1'
import StepTwo from './Components/steps/step2'
import StepThree from './Components/steps/step3'
import StepFour from './Components/steps/step4'

class CreatSMSCampaign extends Component {
    state = {
        step: 1,
        maxSteps: 4,
        EmailCampaignData: {},
    }
    nextStep = () => {
        const { step } = this.state
        this.setState({
            step: step + 1,
        })
    }
    prevStep = () => {
        const { step } = this.state
        this.setState({
            step: step - 1,
        })
    }

    // steps data
    _handleStepSubmit = params => {
        let { EmailCampaignData } = this.state
        EmailCampaignData = _.assign({}, EmailCampaignData, params)
        this.setState({
            EmailCampaignData,
        })
        this.nextStep()
        // call api
    }

    render() {
        const { step, EmailCampaignData } = this.state
        switch (step) {
            case 1:
                return (
                    <>
                        <StepOne
                            data={EmailCampaignData}
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    </>
                )
            case 2:
                return (
                    <StepTwo
                        data={EmailCampaignData}
                        nextStep={this.nextStep}
                        onSubmit={this._handleStepSubmit}
                        prevStep={this.prevStep}
                    />
                )
            case 3:
                return (
                    <StepThree
                        data={EmailCampaignData}
                        nextStep={this.nextStep}
                        onSubmit={this._handleStepSubmit}
                        prevStep={this.prevStep}
                    />
                )
            case 4:
                return (
                    <StepFour
                        nextStep={this.nextStep}
                        onSubmit={this._handleStepSubmit}
                        prevStep={this.prevStep}
                    />
                )
            default:
                return null
        }
    }
}

export default CreatSMSCampaign
