import React from 'react'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'
import { Helmet } from 'react-helmet'
import _ from 'lodash'
import history from '@/utils/history'
import '../index.scss'

// Import all pages
import Login from './Login'
import SelectCampaignType from './Campaign'
import CreatePushNotifications from './PushNotification'
import CreatSMSCampaign from './SMSCampaign'
import CreatEmailCampaign from './EmailCampaign'
import Segementation from './Segmentation'
import Settings from './Settings'

const urlPatterns = [
    {
        path: '/',
        component: Login,
        title: 'Login',
    },
    {
        path: '/segmentation',
        component: Segementation,
        title: 'Segementation',
    },

    {
        path: '/campaign',
        component: SelectCampaignType,
        title: 'Select Campaign',
    },
    {
        path: '/settings',
        component: Settings,
        title: 'Settings',
    },
    {
        path: '/push-notification',
        component: CreatePushNotifications,
        title: 'Push Notification',
    },
    {
        path: '/email',
        component: CreatEmailCampaign,
        title: 'Email Campaign',
    },
    {
        path: '/sms',
        component: CreatSMSCampaign,
        title: 'SMS Campaign',
    },
]

const RootRouter = () => (
    <Router
        history={history}
        // basename="/imt-fe"
    >
        <Switch>
            {_.map(urlPatterns, (url, index) => (
                <Route
                    key={index}
                    path={url.path}
                    exact
                    render={route => (
                        <>
                            <Helmet>
                                <title>{url.title || 'Home'}</title>
                            </Helmet>
                            <url.component {...route} />
                        </>
                    )}
                />
            ))}
            <Route component={() => <div className="pageNotFound" />} />
        </Switch>
    </Router>
)

export default RootRouter
