import React, { Component } from 'react'
import _ from 'lodash'
import { Route } from 'react-router-dom'
import StepOne from './components/steps/step1'
import StepTwo from './components/steps/step2'
import StepThree from './components/steps/step3'
import StepFour from './components/steps/step4'
import StepFive from './components/steps/step5'
import connectComponent from '../../connect'
import './index.scss'

class CreatePushNotifications extends Component {
    state = {
        step: 1,
        maxSteps: 5,
    }

    nextStep = () => {
        const { step } = this.state
        this.setState({
            step: step + 1,
        })
    }
    prevStep = () => {
        const { step } = this.state
        this.setState({
            step: step - 1,
        })
    }

    render() {
        const { history } = this.props
        return (
            <>
                <Route
                    exact
                    path={`${this.props.match.url}`}
                    render={() => <StepOne history={history} />}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-2/:campaignId/`}
                    render={route => <StepTwo history={history} {...route} />}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-3/:campaignId/`}
                    render={route => <StepThree history={history} {...route} />}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-4/:campaignId/`}
                    render={route => <StepFour history={history} {...route} />}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-5/:campaignId/`}
                    render={route => <StepFive history={history} {...route} />}
                />
            </>
        )
    }
}

export default connectComponent(CreatePushNotifications)
