export const ChartConfig = {
    chart: {
        type: 'areaspline',
    },
    title: {
        text: 'Weekly Active User',
    },
    // legend: {
    //     layout: 'vertical',
    //     align: 'left',
    //     verticalAlign: 'top',
    //     x: 150,
    //     y: 100,
    //     floating: true,
    //     borderWidth: 1,
    //     backgroundColor: 'green',
    // },
    xAxis: {
        categories: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'],
        plotBands: [
            {
                // visualize the weekend
                from: 4.5,
                to: 6.5,
                color: '#ffffff',
            },
        ],
    },
    yAxis: {
        title: {
            text: 'Fruit units',
        },
    },
    tooltip: {
        shared: true,
        valueSuffix: ' units',
    },
    credits: {
        enabled: false,
    },
    plotOptions: {
        areaspline: {
            fillOpacity: 0.3,
            color: '#26dc46',
        },
    },
    series: [
        {
            name: 'John',
            data: [10, 23, 34, 50, 40, 30, 22],
        },
    ],
}
