import React, { Component } from 'react'
import _ from 'lodash'
import { Route } from 'react-router-dom'
import StepOne from './Components/steps/step1'
import StepTwo from './Components/steps/step2'
import StepThree from './Components/steps/step3'
import StepFour from './Components/steps/step4'

class CreateSMSCampaign extends Component {
    state = {
        step: 1,
        maxSteps: 4,
        SMSCampaign: {},
    }
    nextStep = () => {
        const { step } = this.state
        this.setState({
            step: step + 1,
        })
        console.log(step, 'step in index ffile')
    }
    prevStep = () => {
        const { step } = this.state
        this.setState({
            step: step - 1,
        })
    }

    // steps data
    _handleStepSubmit = params => {
        let { SMSCampaign } = this.state
        SMSCampaign = _.assign({}, SMSCampaign, params)
        this.setState({
            SMSCampaign,
        })
        this.nextStep()
        // call api
    }

    render() {
        const { SMSCampaign } = this.state
        return (
            <>
                <Route
                    exact
                    path={`${this.props.match.url}`}
                    render={() => (
                        <StepOne
                            data={campaign}
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/stepp2`}
                    render={() => (
                        <StepTwo
                            data={campaign}
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/stepp3`}
                    render={() => (
                        <StepThree
                            data={campaign}
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/4-step`}
                    render={() => (
                        <StepFour
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
            </>
        )
    }
}

export default CreateSMSCampaign
