import React from 'react'
import {Link} from 'react-router-dom'
import Table, { Tcol, Trow, Thead, Tbody } from '../../../../components/table';
import Loader from '../../../../components/loader'
// actions
import {
    toggleAllCheckboxes,
    toggleCheckbox,
    toggleMenu,
    deleteCampaign
} from '../../../../store/actions'

const ListView = ({ data, headers, loading }) => {
    if (loading) {
        return <div className="text-center">
            <Loader inline />
        </div>
    }

    if (!headers.length) {
        return null
    }

    return (
        <Table>
            <Thead>
                <Trow>
                    {headers.map((item, index) => {
                        if (item.checkbox) {
                            return (
                                <Tcol key={index} component="th">
                                    <input
                                        type="checkbox"
                                        checked={item.checked}
                                        value={item.name}
                                        onClick={() => toggleAllCheckboxes(!item.checked, data, headers)}
                                    />
                                </Tcol>
                            )
                        }
                        return (
                            <Tcol key={index} component="th">
                                {item.name}
                            </Tcol>
                        )
                    })}
                </Trow>
            </Thead>
            <Tbody>
                {data.map((item, index) => {
                    return (
                        <RenderRow
                            key={index}
                            row={item}
                            headers={headers}
                            rowIndex={index}
                            data={data}
                        />
                    )
                })}
            </Tbody>
        </Table>
    )
}

const RenderRow = ({ row, headers, rowIndex, data }) => {
    return (
        <Trow>
            <Tcol>
                <input
                    type="checkbox"
                    checked={row.checked}
                    value={row.checked}
                    onClick={
                        () => toggleCheckbox(!row.checked, rowIndex, data, headers)
                    }
                />
            </Tcol>
            <Tcol>
                <Link to={`/campaign/detail/${row.campaign_id}`}>{row.campaign_name}</Link>
            </Tcol>
            <Tcol>
                {row.campaignstats__delivered}
            </Tcol>
            <Tcol>
                {row.campaignstats__unique_opens}
            </Tcol>
            <Tcol>
                {row.campaignstats__unique_clicks}
            </Tcol>
            <Tcol>
                {row.campaignstats__unsubscribes}
            </Tcol>
            <Tcol>
                {row.campaignstats__bounces}
            </Tcol>
            <Tcol>
                <div className="table-menu">
                    <span
                        onClick={() => toggleMenu(rowIndex, !row.showMenu, data)}
                        className="table-menu-button"
                    >
                        <i className="icon ion-md-more" />
                    </span>
                    {row.showMenu
                        && (
                            <div className="table-menu-items">
                                <div className="item" onClick={() => {
                                    deleteCampaign(row.campaign_id, data);
                                    toggleMenu(rowIndex, !row.showMenu, data);
                                }}>
                                    <i className="icon ion-md-trash" />
                                </div>
                            </div>
                        )
                    }
                </div>
            </Tcol>
        </Trow>
    )
}

export default ListView;
