import React from 'react'
import cx from 'classnames'
import Table, { Tcol, Trow, Thead, Tbody } from '../../components/table'
import Loader from '../../components/loader'
// actions
// import {
//     toggleAllCheckboxes,
//     toggleCheckbox,
//     updateParentGroup,
//     toggleGroupMenu,
//     deleteListAction,
// } from '../../store/actions'

const Tables = ({ data, headers, loading }) => {
    if (loading) {
        return (
            <div className="text-center">
                <Loader inline />
            </div>
        )
    }

    if (!headers.length) {
        return null
    }

    return (
        <Table>
            <Thead>
                <Trow>
                    {headers.map((item, index) => {
                        if (item.checkbox) {
                            return (
                                <Tcol key={index} component="th">
                                    <input
                                        type="checkbox"
                                        checked={item.checked}
                                        value={item.name}
                                        // onClick={() =>
                                        //     toggleAllCheckboxes(!item.checked, data, headers)
                                        // }
                                    />
                                </Tcol>
                            )
                        }
                        return (
                            <Tcol key={index} component="th">
                                {item.name}
                            </Tcol>
                        )
                    })}
                </Trow>
            </Thead>
            <Tbody>
                {data.map((item, index) => {
                    return (
                        <Trow key={index}>
                            <RenderRow row={item} headers={headers} rowIndex={index} data={data} />
                        </Trow>
                    )
                })}
            </Tbody>
        </Table>
    )
}

const RenderRow = ({ row, headers, rowIndex, data }) => {
    return headers.map((header, index) => {
        if (header.key === 'checked') {
            return (
                <Tcol key={index}>
                    <input
                        type="checkbox"
                        checked={row[header.key]}
                        value={row[header.key]}
                        // onClick={() => toggleCheckbox(!row[header.key], rowIndex, data, headers)}
                    />
                </Tcol>
            )
        }
        if (header.key === 'contacts') {
            return <Tcol key={index}>{row[header.key]}</Tcol>
        }
        if (header.key === 'type') {
            return (
                <Tcol key={index}>
                    <span className={cx('label', { 'label-green': row[header.key] === 'Global' })}>
                        {row[header.key]}
                    </span>
                </Tcol>
            )
        }
        if (header.key === 'name') {
            return (
                <Tcol key={index}>
                    <span>
                        // onClick={() => updateParentGroup(row)}>{row[header.key]}
                    </span>
                </Tcol>
            )
        }
        if (header.key === 'showMenu') {
            return (
                <Tcol key={index}>
                    <div className="table-menu">
                        <span
                            // onClick={() => toggleGroupMenu(rowIndex, !row[header.key], data)}
                            className="table-menu-button"
                        >
                            <i className="icon ion-md-more" />
                        </span>
                        {row[header.key] && (
                            <div className="table-menu-items">
                                <div
                                    className="item"
                                    // onClick={() => {
                                    //     deleteListAction(row.email_group_id, rowIndex, data)
                                    //     toggleGroupMenu(rowIndex, !row[header.key], data)
                                    // }}
                                >
                                    <i className="icon ion-md-trash" />
                                </div>
                            </div>
                        )}
                    </div>
                </Tcol>
            )
        }
        return <Tcol key={index}>{row[header.key]}</Tcol>
    })
}

export default Tables
