import React from 'react'
import './index.scss'

const Loader = ({ fullScreen, flash }) => {
    if (fullScreen) {
        return (
            <div className="loader">
                <img src={require('../../assets/images/success-image.gif')} alt="" />
            </div>
        )
    }

    return (
        <div className="loader">
            <img src={require('../../assets/images/loader.gif')} alt="" />
        </div>
    )
}

export default Loader
