import React, { Component } from 'react'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import WrapComponents from '@/components/wrapper'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import './index.scss'

class StepFour extends Component {
    state = {
        steps: ['Channel', 'Message', 'Segmentation', 'Scheduling', 'Overview'],
        schedule: false,
        now: true,
        startDate: new Date(),
    }

    handleChange(date) {
        if (date < this.state.startDate)
            this.setState({
                startDate: date,
            })
    }

    saveAndContinue = e => {
        e.preventDefault()
        this.props.nextStep()
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    render() {
        const { steps, schedule, now } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={3} />
                <div>
                    <div className="section-left">
                        <div className="section ">
                            <SmallHeading>Start the Campaign</SmallHeading>
                            <Radio
                                checked={now}
                                onClick={() => this.setState({ now: !now, schedule: !schedule })}
                            >
                                Now
                            </Radio>
                            <Radio
                                checked={schedule}
                                onClick={() => this.setState({ now: !now, schedule: !schedule })}
                            >
                                Send at a specific time
                            </Radio>
                            {schedule && (
                                <div className="datepicker">
                                    <DatePicker
                                        showTimeSelect
                                        timeIntervals={1}
                                        selected={this.state.startDate}
                                        onSelect={this.handleSelect}
                                        onChange={date => this.handleChange(date)}
                                        showTimeSelect
                                        timeFormat="HH:mm"
                                        dateFormat="MMMM d, yyyy h:mm aa"
                                        timeCaption="Time"
                                        peekNextMonth
                                        showMonthDropdown
                                        showYearDropdown
                                        dropdownMode="select"
                                    />
                                </div>
                            )}
                        </div>
                        {/* <div className="section">Show Count</div> */}
                        <div className="section">
                            <SmallHeading>Frequency Capping Rules</SmallHeading>
                            <li>Ignore Frequency Cap</li>
                            <li>
                                Useful for scenarios like sending an App update message, where you
                                don't want the message to be skipped because of Frequency Capping
                            </li>
                        </div>
                        <ColoredButtons onClick={this.handleContinue} className="float">
                            Send
                        </ColoredButtons>
                        <TransparentButtons onClick={this.back} className="float grey-border">
                            Save as Draft
                        </TransparentButtons>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default StepFour
