import Store from '@/reducer'
import Api from '@/utils/Api'
import * as Types from '@/constants/Types'

export const getCampaignListAction = async (data = null) => {
    try {
        Store.dispatch({
            type: Types.CAMPAIGN_LIST_REQUEST,
        })
        const response = await Api.getCampaignList(data)
        Store.dispatch({
            type: Types.CAMPAIGN_LIST_SUCCESS,
            data: response,
        })
    } catch (err) {
        Store.dispatch({
            type: Types.CAMPAIGN_LIST_FAILURE,
            data: err.message,
        })
    }
}
