import React, { Component } from 'react'
import _ from 'lodash'
import { Route } from 'react-router-dom'
import StepOne from './step1'
import StepTwo from './step2'
import StepThree from './step3'
import StepFour from './step4'
import StepFive from './step5'
import './index.scss'

class CreatePushNotifications extends Component {
    state = {
        step: 1,
        maxSteps: 5,
        campaign: {},
    }

    // steps data
    _handleStepSubmit = params => {
        let { campaign } = this.state
        campaign = _.assign({}, campaign, params)
        this.setState({
            campaign,
        })
        this.nextStep()
        // call api
    }

    render() {
        const { campaign } = this.state
        const { history } = this.props
        return (
            <>
                <Route
                    exact
                    path={`${this.props.match.url}`}
                    render={() => (
                        <StepOne
                            history={history}
                            data={campaign}
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-2/`}
                    render={() => (
                        <StepTwo
                            history={history}
                            data={campaign}
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-3/`}
                    render={() => (
                        <StepThree
                            history={history}
                            data={campaign}
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-4/`}
                    render={() => (
                        <StepFour
                            history={history}
                            data={campaign}
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-5/`}
                    render={() => (
                        <StepFive
                            history={history}
                            data={campaign}
                            nextStep={this.nextStep}
                            onSubmit={this._handleStepSubmit}
                            prevStep={this.prevStep}
                        />
                    )}
                />
            </>
        )
    }
}

export default CreatePushNotifications
