import React, { Component } from 'react'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import WrapComponents from '@/components/wrapper'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import { Checkbox } from '@/components/checkBox'
import Stepper from '@/components/stepper'
import './index.scss'

class StepFour extends Component {
    constructor(props) {
        super(props)
        this.state = {
            startDate: new Date(),
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(date) {
        this.setState({
            startDate: date,
        })
    }
    state = {
        steps: ['Channel', 'Message', 'Segmentation', 'Scheduling', 'Overview'],
        schedule: 'now',
    }
    saveAndContinue = e => {
        e.preventDefault()
        this.props.data.push(this.state)
        this.props.nextStep()
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    render() {
        const { steps } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={3} />
                <div>
                    <div className="section-left">
                        <div className="section ">
                            <SmallHeading>Start the Campaign</SmallHeading>
                            <Radio>Now</Radio>
                            <Radio>Send at a specific time</Radio>
                            <DatePicker
                                selected={this.state.startDate}
                                onChange={this.handleChange}
                                showTimeSelect
                                // excludeTimes={[
                                //     setHours(setMinutes(new Date(), 0), 17),
                                //     setHours(setMinutes(new Date(), 30), 18),
                                //     setHours(setMinutes(new Date(), 30), 19),
                                //     setHours(setMinutes(new Date(), 30), 17),
                                // ]}
                                dateFormat="MMMM d, yyyy h:mm aa"
                            />

                            <div className="devider-line" />
                            <Checkbox
                            // onClick={e => {
                            //     this.toggleState(!advance)
                            // }}
                            >
                                Exclude Users
                            </Checkbox>
                        </div>
                        <div className="section">Show Count</div>
                        <ColoredButtons onClick={this.saveAndContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons onClick={this.back} className="float">
                            Save as Draft
                        </TransparentButtons>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default StepFour
