import React, { Component } from 'react'
import { Radio } from '@/components/radio'
// import { SmallHeading } from '@/components/headings/index'
import { ColoredButtons } from '@/components/button/index'

class MobilePush extends Component {
    state = {
        tabOne: true,
        tabTwo: false,
    }
    render() {
        const { tabOne, tabTwo } = this.state
        return (
            <div className="section">
                <Radio
                    checked={tabOne}
                    onClick={() => this.setState({ tabTwo: !tabTwo, tabOne: !tabOne })}
                >
                    Android
                </Radio>
                <Radio
                    checked={tabTwo}
                    onClick={() => this.setState({ tabTwo: !tabTwo, tabOne: !tabOne })}
                >
                    iOS
                </Radio>
                {tabOne && (
                    <div className="mrgn-top-15">
                        <h6>Server key (See our documentation to know more.)</h6>
                        <input className="form-input" placeholder="Server Key" />
                        <h6>Additional Server Key (Optional)</h6>
                        <input
                            style={{ width: '50%', display: 'block' }}
                            className="form-input"
                            placeholder="Enter Additional Server Key"
                        />
                        <h6>Package Name (Optional)</h6>
                        <input
                            style={{ width: '50%', display: 'block' }}
                            className="form-input"
                            placeholder="Enter Package Name"
                        />
                        <ColoredButtons className="small-btn">Save</ColoredButtons>
                    </div>
                )}
                {tabTwo && <div>Need to discuss with Backend</div>}
            </div>
        )
    }
}

export default MobilePush
