import React, { Component } from 'react'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { Radio } from '@/components/radio'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import Alert from '@/components/alert'
import { SmallHeading } from '@/components/headings/index'
import ConnectComponent from '../../../connect'
import { Checkbox } from '@/components/checkBox/index'

class StepOne extends Component {
    state = {
        source: 'toko-cm',
        type: 'General',
        campaignType: 'general',
        deviceType: 'in-app',
        userStatus: '',
        channel: 'Digital',
        android: true,
        iOS: false,
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        name: '',
        nameError: false,
    }

    // async componentDidMount() {
    //     const { search } = this.props.location
    //     let campaignId = null
    //     if (search && search.indexOf('id=') > -1) {
    //         campaignId = search.split('id=')[1]
    //     }
    //     this.setState({
    //         campaignId,
    //     })
    //     if (!campaignId) {
    //         createParentId()
    //     }
    // }

    handleInputFields(key, value) {
        this.setState({
            [key]: value,
        })
    }

    // onSubmit = () => {
    //     let { name } = this.state
    //     let anyError = false
    //     if (name === '') {
    //         this.setState({
    //             nameError: true,
    //         })
    //         anyError = true
    //     }
    //     if (!anyError) {
    //         const { steps, ...rest } = this.state
    //         const { parentID } = this.props.CreateCampaign.data
    //         CreateCampaignAction({ parentID, ...rest, next: 'step-2' })
    //         // history.push(`/email/step-2/${campaignId}`)
    //     }
    // }

    render() {
        const { steps, name, iOS, android, nameError } = this.state
        // const { loading, error } = this.props.CreateCampaign
        // if (loading) {
        //     return <Loader />
        // }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={0} />
                <div className="mrgn-btm">
                    <span>Channel :</span>
                    <span style={{ color: '#6cae53' }}>In-App</span>
                </div>
                <div className="section">
                    {nameError && <Alert className="alert">*This field is required</Alert>}
                    <SmallHeading>Campaign Name*</SmallHeading>
                    <input
                        placeholder="Enter Campaign Name"
                        type="text"
                        className="form-input"
                        name="name"
                        value={name}
                        required
                        onChange={e => this.handleInputFields('name', e.target.value)}
                    />
                </div>
                <div className="section">
                    <SmallHeading>Platforms</SmallHeading>
                    <Checkbox
                        checked={iOS}
                        onClick={() => this.setState({ iOS: !iOS, android: !android })}
                    >
                        iOS
                    </Checkbox>
                    <Checkbox
                        checked={android}
                        onClick={() => this.setState({ iOS: !iOS, android: !android })}
                    >
                        Android
                    </Checkbox>
                </div>
                <div className="section">
                    <SmallHeading>Campaign Type</SmallHeading>
                    <Radio
                        checked={iOS}
                        onClick={() => this.setState({ iOS: !iOS, android: !android })}
                    >
                        iOS
                    </Radio>
                    <Radio
                        checked={android}
                        onClick={() => this.setState({ iOS: !iOS, android: !android })}
                    >
                        Full Screen Pop-Up
                    </Radio>
                    <Radio
                        checked={android}
                        onClick={() => this.setState({ iOS: !iOS, android: !android })}
                    >
                        Border Top
                    </Radio>
                    <Radio
                        checked={android}
                        onClick={() => this.setState({ iOS: !iOS, android: !android })}
                    >
                        Border Bottom
                    </Radio>
                </div>

                <ColoredButtons className="float">Continue</ColoredButtons>
                <TransparentButtons
                    onClick={() => this.props.history.goBack()}
                    className="float grey-border"
                >
                    Go Back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepOne)
