import React from 'react'
import { Checkbox } from '@/components/checkBox'
import { Radio } from '@/components/radio/index'
import '../TabAndriod/index.scss'
import plusIcon from '../../../../assets/images/add.svg'
import { SmallHeading } from '@/components/headings/index'

class TabWeb extends React.Component {
    state = {
        iconImage: false,
        imageUrl: false,
        image: false,
        notificationSound: false,
        media: '',
        appLink: '',
        customValues: [
            {
                key: '',
                value: '',
                canAddMore: '',
            },
        ],
        actionButtons: [
            {
                text: '',
                appLink: '',
                icon: '',
                canAddMore: true,
            },
        ],
    }

    handleInputFields(key, value) {
        if (this.props.onChange) {
            this.props.onChange(key, value)
        }
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }
    _addPairs = () => {
        const { customValues } = this.state
        customValues.push({
            key: '',
            value: '',
            canAddMore: false,
        })
        this.setState({
            customValues: customValues.map((part, index) => {
                if (index === customValues.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }
    _addMoreActions = () => {
        const { actionButtons } = this.state
        actionButtons.push({
            text: '',
            appLink: '',
            canAddMore: false,
            icon: '',
        })
        this.setState({
            actionButtons: actionButtons.map((part, index) => {
                if (index === actionButtons.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }

    _handlePairsInput = (index, key, value) => {
        const { customValues } = this.state
        customValues[index][key] = value
        this.setState({ customValues })
    }
    _handleActionsInput = (index, key, value) => {
        const { actionButtons } = this.state
        actionButtons[index][key] = value
        this.setState({ actionButtons })
    }

    _removePairs = index => {
        const { customValues } = this.state
        customValues.splice(index, 1)
        this.setState({ customValues })
    }

    _removeAction = index => {
        const { actionButtons } = this.state
        actionButtons.splice(index, 1)
        this.setState({ actionButtons })
    }

    render() {
        const {
            iconImageUrl,
            imageUrl,
            iconImage,
            image,
            media,
            appLink,
            actionButtons,
        } = this.state

        return (
            <div className="section section-grid">
                <div className="advance-row">
                    <Checkbox
                        checked={iconImage}
                        onChange={() => this.setState({ iconImage: !iconImage })}
                    >
                        Icon Image
                    </Checkbox>
                    {iconImage && (
                        <div className="mrgn-top-15 margn-left-15 ">
                            {/* <Radio checked="true">Upload Image</Radio> */}
                            <Radio
                                checked={iconImageUrl}
                                onClick={() => this.setState({ iconImageUrl: !iconImageUrl })}
                            >
                                Default Icon
                            </Radio>
                        </div>
                    )}
                </div>

                <div className="advance-row">
                    <Checkbox checked={image} onChange={() => this.setState({ image: !image })}>
                        Image
                    </Checkbox>
                    {image && (
                        <div className="mrgn-top-15 margn-left-15 ">
                            {/* <Radio checked="true">Upload Image</Radio> */}
                            <Radio
                                checked={imageUrl}
                                onClick={() => this.setState({ imageUrl: !imageUrl })}
                            >
                                Enter URL
                            </Radio>
                            {imageUrl && (
                                <div className="mrgn-top-15 row-child">
                                    <input
                                        type="text"
                                        className="form-input"
                                        placeholder="Enter URL"
                                        name="media"
                                        value={media}
                                        onChange={e =>
                                            this.handleInputFields('media', e.target.value)
                                        }
                                    />
                                    <h6 className="input-footer">File supported: jpeg and png </h6>
                                </div>
                            )}
                        </div>
                    )}
                </div>

                <div className="divider-line" />
                <div>
                    <SmallHeading>Actions *</SmallHeading>
                    <h6>
                        Choose what happens when user clicks the body of this push notification.
                    </h6>
                    <div className="flex">
                        <input
                            className="form-input mrgn-10"
                            placeholder="Enter Deeplinking URL"
                            name="appLink"
                            value={appLink}
                            onChange={e => this.handleInputFields('appLink', e.target.value)}
                        />
                    </div>
                    <h5>Add Action buttons(Optional):</h5>
                    {this.state.actionButtons.map((part, index) => (
                        <div key={index} className="flex-space-bt">
                            <input
                                value={part.text}
                                onChange={e =>
                                    this._handlePairsInput(index, 'text', e.currentTarget.value)
                                }
                                className="form-input mrgn-10"
                                placeholder="Action button text"
                            />
                            <input
                                className="form-input mrgn-10"
                                value={part.appLink}
                                onChange={e =>
                                    this._handlePairsInput(index, 'appLink', e.currentTarget.value)
                                }
                                placeholder="Action button applink"
                            />
                            <input
                                value={part.icon}
                                onChange={e =>
                                    this._handlePairsInput(index, 'icon', e.currentTarget.value)
                                }
                                className="form-input mrgn-10"
                                placeholder="Action button icon url"
                            />

                            {part.canAddMore ? (
                                <div className="plus" onClick={this._addPairs}>
                                    <img src={plusIcon} alt="" />
                                </div>
                            ) : (
                                <div className="minus" onClick={() => this._removePairs(index)}>
                                    -
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        )
    }
}

export default TabWeb
