import Api from '@/utils/Api'
import Store from '@/reducer'
import * as Types from '@/constants/Types'

export const AddUser = async params => {
    try {
        const response = await Api.addCampaignUser(params)
        return response
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: 'Something went wrong in adding user in Campaign',
        })
        return null
    }
}

export const SendCampaign = async params => {
    try {
        const response = await Api.triggerCampaign(params)
        return response
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: 'Oops! No response, Something went wrong.',
        })
        return null
    }
}

export const UploadContent = async (file, category = 'email_template') => {
    try {
        const formData = new FormData()
        formData.append('file', file)
        formData.append('category', category)
        return await Api.CreateContent(formData)
    } catch (e) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: 'Something went wrong in uploading content',
        })
        return null
    }
}

export const createParentId = async () => {
    try {
        const response = await Api.createParent() // TODO: uncomment before build
        // const response = { parentID: 35 }
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_SUCCESS,
            data: response,
        })
        return response
    } catch (e) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: 'Oops! No response, Something went wrong.',
        })
        return null
    }
}

export const GetTimeString = date => {
    if (!date) {
        return ''
    }
    date = new Date(date)
    return `${date.toDateString()}, ${date.toTimeString()}`
}
