import React, { Component } from 'react'
import _ from 'lodash'
import connectComponent from '@/connect'
import WrapComponents from '@/components/wrapper'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Checkbox } from '@/components/checkBox'
import Stepper from '@/components/stepper'
import Alert from '@/components/alert'
import Loader from '@/components/loader'
import './index.scss'
import androidMobile from '../../assets/images/andriod.png'
import webImage from '../../assets/images/webImage.jpg'
import iOSImage from '../../assets/images/iOSImage.jpg'
import TabAndroid from './components/TabAndriod'
import TabWeb from './components/TabWeb'
import TabiOS from './components/TabiOS'
import { capitalizeFirstLetter } from '@/actions/utils'
import { getParentDetails } from '@/actions/CreateCampaign/PnCampaign'
import { UpdateCampaignBulk } from '@/actions/CreateCampaign/PnCampaign'

const getBgStyle = image => {
    if (!image) {
        return null
    }
    return { backgroundImage: `url(${image})` }
}

class StepTwo extends Component {
    state = {
        parentID: '',
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview'],
        title: '',
        message: '',
        advance: false,
        error: false,
        activeBar: 'android',
        childParams: {},
    }

    componentWillMount = async () => {
        let {
            params: { parent_id },
        } = this.props.match
        parent_id = parseInt(parent_id, 10)
        this.setState({
            parentID: parent_id,
        })
        const response = await getParentDetails(parent_id)
        const temp = _.head(response)
        if (temp) {
            this.setState({
                title: temp.title,
                message: temp.message,
            })
        }
    }

    handleContinue = () => {
        const { data } = this.props.CreateCampaign
        const { title, message, parentID, childParams } = this.state
        const params = {
            title,
            message,
            parentID,
            ...childParams,
        }
        let anyError = false
        if (title === '') {
            this.setState({
                error: true,
            })
            anyError = true
        }
        if (message === '') {
            this.setState({
                error: true,
            })
            anyError = true
        }
        if (!anyError) {
            const requests = []
            const next = `/push-notification/step-3/${parentID}`
            if (_.isArray(data)) {
                _.forEach(data, part => {
                    requests.push({
                        ...part,
                        ...params,
                    })
                })
            }
            UpdateCampaignBulk(requests, next)
        }
    }

    handleAdvanceInput = (key, value) => {
        const { childParams } = this.state
        this.setState({
            childParams: _.assign({}, childParams, {
                [key]: value,
                type: 'Image',
            }),
        })
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    toggleState(advance) {
        this.setState({ advance })
    }

    switchTabs = activeBar => {
        this.setState({
            activeBar,
        })
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    render() {
        const {
            steps,
            title,
            message,
            advance,
            activeBar,
            error,
            childParams: { media },
        } = this.state
        const { CreateCampaign } = this.props
        if (CreateCampaign.loading) {
            return <Loader />
        }
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={1} />
                {error && <Alert>Error: *Please fill required value</Alert>}
                {CreateCampaign.error && <Alert>{CreateCampaign.error}</Alert>}
                <div className="flex-space-bt ">
                    <div className="section-left">
                        <div className="section">
                            <SmallHeading>Title*</SmallHeading>
                            <input
                                placeholder="Enter Message title"
                                type="text"
                                className="form-input"
                                name="title"
                                value={title}
                                onChange={e =>
                                    this.handleInputFields('title', e.currentTarget.value)
                                }
                            />
                            <SmallHeading>Message*</SmallHeading>
                            <input
                                placeholder="Enter Message"
                                type="text"
                                className="form-input"
                                name="message"
                                value={message}
                                onChange={e =>
                                    this.handleInputFields('message', e.currentTarget.value)
                                }
                            />
                            <Checkbox
                                onClick={e => {
                                    this.toggleState(!advance)
                                }}
                            >
                                Advanced
                            </Checkbox>
                        </div>
                        {advance && (
                            <>
                                <div className="flex">
                                    {_.map(_.get(CreateCampaign, 'data'), (part, i) => (
                                        <div
                                            key={i}
                                            className={`type ${
                                                activeBar === part.deviceType ? 'active' : ''
                                            }`}
                                            onClick={() => this.switchTabs(part.deviceType)}
                                        >
                                            {capitalizeFirstLetter(part.deviceType)}
                                        </div>
                                    ))}
                                </div>
                                <div>
                                    {activeBar === 'android' && (
                                        <TabAndroid onChange={this.handleAdvanceInput} />
                                    )}
                                    {activeBar === 'ios' && (
                                        <TabiOS onChange={this.handleAdvanceInput} />
                                    )}
                                    {activeBar === 'web' && (
                                        <TabWeb onChange={this.handleAdvanceInput} />
                                    )}
                                </div>
                            </>
                        )}
                        <ColoredButtons onClick={this.handleContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons
                            onClick={() => this.props.history.goBack()}
                            className="float grey-border"
                        >
                            Go Back
                        </TransparentButtons>
                    </div>
                    <div className="section section-right">
                        <SmallHeading>Preview</SmallHeading>
                        <div className="preview-text">
                            Actual display may be different to this preview depending on the user’s
                            device.
                        </div>
                        {activeBar === 'android' && (
                            <div
                                className="preview-container"
                                style={{ backgroundImage: `url(${androidMobile})` }}
                            >
                                <div className="preview-wrap android-main">
                                    <div className="preview-content">
                                        <div className="preview-header">
                                            Tokopedia • Summary • Now
                                        </div>
                                        <div className="row">
                                            <div className="col-8">
                                                <div className="preview-title">{title}</div>
                                                <div className="preview-msg">{message}</div>
                                            </div>
                                            <div className="col-4 text-right">
                                                <div
                                                    className="pn-image"
                                                    style={getBgStyle(media)}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}
                        {activeBar === 'ios' && (
                            <div
                                className="preview-container"
                                style={{ backgroundImage: `url(${iOSImage})` }}
                            >
                                <div className="preview-wrap ios">
                                    <div className="preview-content">
                                        <div className="preview-header">
                                            Tokopedia • Summary • Now
                                        </div>
                                        <div className="row">
                                            <div className="col-8">
                                                <div className="preview-title">{title}</div>
                                                <div className="preview-msg">{message}</div>
                                            </div>
                                            <div className="col-4 text-right">
                                                <div
                                                    className="pn-image"
                                                    style={getBgStyle(media)}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}
                        {activeBar === 'web' && (
                            <div
                                className="preview-container"
                                style={{ backgroundImage: `url(${webImage})` }}
                            >
                                <div className="preview-wrap web">
                                    <div className="preview-content">
                                        <div className="row">
                                            <div className="col preview-image">
                                                <div
                                                    className="pn-image"
                                                    style={getBgStyle(media)}
                                                />
                                            </div>
                                            <div className="col-8">
                                                <div className="preview-title">{title}</div>
                                                <div className="preview-msg">{message}</div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default connectComponent(StepTwo)
