import React, { Component } from 'react'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { Radio } from '@/components/radio'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import Alert from '@/components/alert'
import { SmallHeading } from '@/components/headings/index'
import ConnectComponent from '../../../connect'
import { Checkbox } from '@/components/checkBox/index'

class StepOne extends Component {
    state = {
        source: 'toko-cm',
        type: 'interstitial',
        deviceType: 'in-app',
        userStatus: '',
        channel: 'Digital',
        android: true,
        iOS: false,
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        name: '',
        nameError: false,
    }
    componentDidMount() {
        createParentId()
    }

    handleInputFields(key, value) {
        this.setState({
            [key]: value,
        })
    }

    handleContinue = () => {
        const {
            android,
            ios,
            web,
            source,
            type,
            campaignType,
            userStatus,
            channel,
            name,
        } = this.state
        const { parentID } = this.props.CreateCampaign.data

        const params = {
            source,
            type,
            campaignType,
            userStatus,
            channel,
            name,
            parentID,
        }

        const requests = []
        if (android) {
            requests.push({
                ...params,
                deviceType: 'android',
            })
        }
        if (ios) {
            requests.push({
                ...params,
                deviceType: 'ios',
            })
        }
        let anyError = false
        if (name === '') {
            this.setState({
                error: true,
            })
            anyError = true
        }
        if (!anyError) {
            CreateCampaignBulk(requests, '/push-notification/step-2/' + parentID)
        }
    }

    render() {
        const { steps, name, iOS, android, nameError, type } = this.state
        // const { loading, error } = this.props.CreateCampaign
        // if (loading) {
        //     return <Loader />
        // }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={0} />
                <div className="mrgn-btm">
                    <span>Channel :</span>
                    <span style={{ color: '#6cae53' }}>In-App</span>
                </div>
                <div className="section">
                    {nameError && <Alert className="alert">*This field is required</Alert>}
                    <SmallHeading>Campaign Name*</SmallHeading>
                    <input
                        placeholder="Enter Campaign Name"
                        type="text"
                        className="form-input"
                        name="name"
                        value={name}
                        required
                        onChange={e => this.handleInputFields('name', e.target.value)}
                    />
                </div>
                <div className="section">
                    <SmallHeading>Platforms</SmallHeading>
                    <Checkbox
                        checked={iOS}
                        onClick={() => this.setState({ iOS: !iOS, android: !android })}
                    >
                        iOS
                    </Checkbox>
                    <Checkbox
                        checked={android}
                        onClick={() => this.setState({ iOS: !iOS, android: !android })}
                    >
                        Android
                    </Checkbox>
                </div>
                <div className="section">
                    <SmallHeading>Campaign Type</SmallHeading>
                    <Radio
                        checked={type === 'interstitial'}
                        name="campaignType"
                        onClick={() => this.setState({ type: 'interstitial' })}
                    >
                        Interstitial Pop-Up
                    </Radio>
                    <Radio
                        checked={type === 'full_screen'}
                        name="campaignType"
                        onClick={() => this.setState({ type: 'full_screen' })}
                    >
                        Full Screen Pop-Up
                    </Radio>
                    <Radio
                        checked={type === 'borderTop'}
                        name="campaignType"
                        onClick={() => this.setState({ type: 'borderTop' })}
                    >
                        Border Top
                    </Radio>
                    <Radio
                        checked={type === 'borderBottom'}
                        name="campaignType"
                        onClick={() => this.setState({ type: 'borderBottom' })}
                    >
                        Border Bottom
                    </Radio>
                    <Radio
                        checked={type === 'alert'}
                        name="campaignType"
                        onClick={() => this.setState({ type: 'alert' })}
                    >
                        Alert
                    </Radio>
                </div>

                <ColoredButtons as="link" to="/in-app/step-2" className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons
                    onClick={() => this.props.history.goBack()}
                    className="float grey-border"
                >
                    Go Back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepOne)
