import React, { Component } from 'react'
import './index.scss'

export const Switch = ({ label, children }) => {
    return (
        <div>
            <input
                // checked={checked}
                // onClick={() => {
                //     this.setState({ checked: !checked })
                // }}
                type="checkbox"
                id="id-name--1"
                name="set-name"
                class="switch-input"
            />
            <label htmlFor="id-name--1" className="switch-label">
                <div className="label">{label}</div>
            </label>
        </div>
    )
}
