import React from 'react'
import { Checkbox } from '@/components/checkBox'
import { Radio } from '@/components/radio/index'
import _ from 'lodash'
import { Tab } from 'semantic-ui-react'
import { SmallHeading } from '@/components/headings/index'
import { ImagePreview } from '@/components/imagePreview'
import '../TabAndriod/index.scss'
import { UploadContent } from '@/actions/utils'
import plusIcon from '../../../../assets/images/add.svg'
import Select from 'react-select'
import Alert from '@/components/alert'

export const NotificationSoundOPtions = [
    { value: 'Default', label: 'Default', color: '#00B8D9' },
    { value: 'tokopedia_endtune', label: 'Tokopedia tune', color: '#00B8D9' },
]

const getBgStyle = image => {
    if (!image) {
        return null
    }
    return { backgroundImage: `url(${image})` }
}

class TabAndroid extends React.Component {
    state = {
        media: '',
        coupons: false,
        image: false,
        upload: false,
        IconUrl: false,
        SoundUrl: false,
        defaultOSSound: true,
        notificationSound: '',
        NotificationSoundOptions: [
            { value: 'Default', label: 'Default' },
            { value: 'iOS', label: 'iOS' },
        ],
        android: false,
        ImageUpload: true,
        priority: 'high',
        media: '',
        sound: '',
        appLink: '',
        addAction: false,
        carousel: [
            {
                image: '',
                text: '',
                url: '',
            },
            {
                image: '',
                text: '',
                url: '',
            },
            {
                image: '',
                text: '',
                url: '',
            },
            {
                image: '',
                text: '',
                url: '',
            },
            {
                image: '',
                text: '',
                url: '',
            },
            {
                image: '',
                text: '',
                url: '',
            },
        ],
        currentCarousel: 0,

        customValues: [
            {
                key: '',
                value: '',
                canAddMore: true,
            },
        ],
        actionButtons: [
            {
                text: '',
                appLink: '',
                icon: '',
                canAddMore: true,
            },
        ],
    }
    handleDripLevelChange = selected => {
        this.setState({
            notificationSound: selected,
        })
    }

    handleFileChange = async e => {
        const { files } = e.target
        const res = await UploadContent(files[0], 'image')
        if (res) {
            this.setState({
                media: res.s3url,
                upload: true,
            })
            if (this.props.onChange) {
                this.props.onChange('media', res.s3url)
            }
        }
    }

    handleInputFields(key, value) {
        if (this.props.onChange) {
            this.props.onChange(key, value)
        }

        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    // push Additional data in actionButtons
    _addPairs = () => {
        const { customValues } = this.state
        customValues.push({
            key: '',
            value: '',
            canAddMore: false,
        })
        this.setState({
            customValues: customValues.map((part, index) => {
                if (index === customValues.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }
    _addMoreActions = () => {
        const { actionButtons } = this.state
        actionButtons.push({
            text: '',
            appLink: '',
            canAddMore: false,
            icon: '',
        })
        this.setState({
            actionButtons: actionButtons.map((part, index) => {
                if (index === actionButtons.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }

    _handlePairsInput = (index, key, value) => {
        const { customValues } = this.state
        customValues[index][key] = value
        this.setState({ customValues })
    }
    _handleActionsInput = (index, key, value) => {
        const { actionButtons } = this.state
        actionButtons[index][key] = value
        this.setState({ actionButtons })
    }

    _removePairs = index => {
        const { customValues } = this.state
        customValues.splice(index, 1)
        this.setState({ customValues })
    }

    _removeAction = index => {
        const { actionButtons } = this.state
        actionButtons.splice(index, 1)
        this.setState({ actionButtons })
    }

    handleCarouselInput = (carousel, path, value) => {
        this.setState({
            carousel: _.set(carousel, path, value),
        })
    }

    handleCarouseFileChange = async (carousel, path, e) => {
        const { files } = e.target
        const res = await UploadContent(files[0], 'image')
        if (res) {
            this.handleCarouselInput(carousel, path, res.s3url)
        }
    }
    handleTabChange = (e, { activeIndex }) => this.setState({ currentCarousel: activeIndex })

    render() {
        const {
            image,
            ImageUrl,
            appLink,
            ImageUpload,
            actionButtons,
            addAction,
            media,
            upload,
            carousel,
            currentCarousel,
        } = this.state
        const { type } = this.props

        console.log(type, 'type of campign is here in ios')

        return (
            <div className="section section-grid">
                <div className="advance-row">
                    <Checkbox checked={image} onChange={() => this.setState({ image: !image })}>
                        Image
                    </Checkbox>
                    {image && (
                        <div className="mrgn-top-15 margn-left-15 ">
                            <Radio
                                checked={ImageUpload}
                                onChange={() =>
                                    this.setState({
                                        ImageUpload: !ImageUpload,
                                    })
                                }
                            >
                                Upload Image
                            </Radio>
                            <Radio
                                checked={!ImageUpload}
                                onClick={() =>
                                    this.setState({
                                        ImageUpload: !ImageUpload,
                                    })
                                }
                            >
                                Enter URL
                            </Radio>
                            {!ImageUpload && (
                                <div className="mrgn-top-15 row-child">
                                    <input
                                        value={media}
                                        name="media"
                                        type="text"
                                        className="form-input"
                                        placeholder="Enter URL"
                                        onChange={e =>
                                            this.handleInputFields('media', e.target.value)
                                        }
                                    />
                                    <h6 className="input-footer">File supported: jpeg and png </h6>
                                </div>
                            )}
                            {ImageUpload && (
                                <>
                                    {type === 'general' && (
                                        <div className="mrgn-top-15 row-child">
                                            {media == '' ? (
                                                <div className="upload-file upload-box dashline-border ">
                                                    <input
                                                        type="file"
                                                        className="file-input"
                                                        id="uploadFile"
                                                        onChange={this.handleFileChange}
                                                        accept=".jpg"
                                                    />
                                                </div>
                                            ) : (
                                                <ImagePreview
                                                    media={media}
                                                    onClick={() => this.setState({ media: '' })}
                                                />
                                            )}
                                        </div>
                                    )}
                                    {type === 'carousel' && (
                                        <div className="mrgn-top-15">
                                            <Tab
                                                activeIndex={currentCarousel}
                                                onTabChange={this.handleTabChange}
                                                panes={carousel.map((tab, index) => ({
                                                    menuItem: `Slide ${index + 1}`,
                                                    render: () => (
                                                        <Tab.Pane>
                                                            <div>
                                                                <input
                                                                    className="form-input"
                                                                    placeholder="Text"
                                                                    value={tab.text}
                                                                    onChange={e =>
                                                                        this.handleCarouselInput(
                                                                            carousel,
                                                                            `[${index}].text`,
                                                                            e.target.value
                                                                        )
                                                                    }
                                                                />
                                                                <input
                                                                    className="form-input"
                                                                    placeholder="Url"
                                                                    value={tab.url}
                                                                    onChange={e =>
                                                                        this.handleCarouselInput(
                                                                            carousel,
                                                                            `[${index}].url`,
                                                                            e.target.value
                                                                        )
                                                                    }
                                                                />
                                                                <div className="upload-file upload-box dashline-border">
                                                                    <input
                                                                        type="file"
                                                                        className="file-input"
                                                                        id={`uploadFile_${index}`}
                                                                        onChange={e =>
                                                                            this.handleCarouseFileChange(
                                                                                carousel,
                                                                                `[${index}].image`,
                                                                                e
                                                                            )
                                                                        }
                                                                        accept="image/*"
                                                                    />
                                                                    {tab.image && (
                                                                        <ImagePreview
                                                                            media={tab.image}
                                                                            onClick={() =>
                                                                                this.handleCarouselInput(
                                                                                    carousel,
                                                                                    `[${index}].image`,
                                                                                    ''
                                                                                )
                                                                            }
                                                                        />
                                                                    )}
                                                                </div>
                                                            </div>
                                                        </Tab.Pane>
                                                    ),
                                                }))}
                                            />
                                        </div>
                                    )}
                                    {type === 'grid' && <div>grid image here</div>}
                                </>
                            )}
                            {upload && (
                                <Alert className="success">
                                    file has been uploaded successfully!
                                </Alert>
                            )}
                        </div>
                    )}
                </div>
                <div className="advance-row">
                    <SmallHeading>Select Notification Sound</SmallHeading>
                    <Select
                        closeMenuOnSelect={false}
                        isMulti={false}
                        options={NotificationSoundOPtions}
                        placeholder="Select notification sound"
                        theme={theme => ({
                            ...theme,
                            borderRadius: 0,
                            colors: {
                                ...theme.colors,
                                text: '#f1f1f1',
                                primary25: '#74d69e',
                                primary: '#74d69e',
                            },
                        })}
                    />
                </div>
                <div className="divider-line" />
                <div>
                    <SmallHeading>Actions</SmallHeading>
                    <h6>
                        Choose what happens when user clicks the body of this push notification.
                    </h6>
                    <div className="advance-row">
                        <input
                            name="appLink"
                            value={appLink}
                            onChange={e => this.handleInputFields('appLink', e.target.value)}
                            className="form-input mrgn-10 width"
                            placeholder="Enter Deeplinking URL"
                        />
                    </div>
                    <SmallHeading>Additional Key Value pairs (Optional):</SmallHeading>
                    {this.state.customValues.map((part, index) => (
                        <div key={index} className="flex-space-bt">
                            <input
                                value={part.key}
                                onChange={e => this._handlePairsInput(index, 'key', e.target.value)}
                                className="form-input mrgn-10"
                                placeholder="key"
                            />
                            <input
                                className="form-input mrgn-10"
                                value={part.value}
                                onChange={e =>
                                    this._handlePairsInput(index, 'value', e.currentTarget.value)
                                }
                                placeholder="value"
                            />
                            {part.canAddMore ? (
                                <div className="plus" onClick={this._addPairs}>
                                    <img src={plusIcon} alt="" />
                                </div>
                            ) : (
                                <div className="minus" onClick={() => this._removePairs(index)}>
                                    -
                                </div>
                            )}
                        </div>
                    ))}

                    <h4
                        className="green-text-add"
                        onClick={() => this.setState({ addAction: !addAction })}
                    >
                        Add Actions +
                    </h4>
                    {addAction && (
                        <>
                            {actionButtons.map((part, index) => (
                                <div key={index} className="flex-space-bt">
                                    <input
                                        value={part.text}
                                        onChange={e =>
                                            this._handleActionsInput(index, 'text', e.target.value)
                                        }
                                        className="form-input mrgn-10"
                                        placeholder="Action button text"
                                    />
                                    <input
                                        className="form-input mrgn-10"
                                        value={part.appLink}
                                        onChange={e =>
                                            this._handleActionsInput(
                                                index,
                                                'appLink',
                                                e.currentTarget.value
                                            )
                                        }
                                        placeholder="Action button applink"
                                    />
                                    <input
                                        className="form-input mrgn-10"
                                        value={part.icon}
                                        onChange={e =>
                                            this._handleActionsInput(
                                                index,
                                                'icon',
                                                e.currentTarget.value
                                            )
                                        }
                                        placeholder="Action button icon url"
                                    />
                                    {part.canAddMore ? (
                                        <>
                                            {actionButtons.length < 2 ? (
                                                <div
                                                    className="plus"
                                                    onClick={this._addMoreActions}
                                                >
                                                    <img src={plusIcon} alt="" />
                                                </div>
                                            ) : (
                                                ''
                                            )}
                                        </>
                                    ) : (
                                        <div
                                            className="minus"
                                            onClick={() => this._removeAction(index)}
                                        >
                                            -
                                        </div>
                                    )}
                                </div>
                            ))}
                        </>
                    )}
                </div>
            </div>
        )
    }
}

export default TabAndroid
