import React, { Component } from 'react'
import _ from 'lodash'
import history from '@/utils/history'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import WrapComponents from '@/components/wrapper'
import Loader from '@/components/loader'
import Alert from '@/components/alert'
import ConnectComponent from '@/connect'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import { UpdateCampaignData, GetCampaign } from '@/actions/CreateCampaign/emailCampaign'
import './index.scss'

class StepThree extends Component {
    state = {
        campaignId: '',
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        schedule: false,
        now: true,
        startDate: new Date(),
        selectedDate: '',
        selectDateError: false,
    }

    async componentWillMount() {
        const {
            params: { campaignId },
        } = this.props.match
        this.setState({
            campaignId,
        })
        await GetCampaign(campaignId)
    }

    handleChange(date) {
        this.setState({
            startDate: date,
        })
    }

    handleContinue = () => {
        const { startDate, campaignId } = this.state
        const { data } = this.props.CreateCampaign
        UpdateCampaignData({
            ..._.assign({}, data, { scheduledTime: startDate.toJSON() }),
        })
        history.push(`/sms/step-4/${campaignId}`)
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    render() {
        const { steps, schedule, now, startDate } = this.state
        const { CreateCampaign } = this.props
        if (CreateCampaign.loading) {
            return <Loader />
        }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={2} />
                <div>
                    {CreateCampaign.error && <Alert>Error: {CreateCampaign.error}</Alert>}
                    <div className="section-left">
                        <div className="section ">
                            <SmallHeading>Start the Campaign</SmallHeading>
                            <Radio
                                checked={now}
                                onChange={() => this.setState({ now: !now, schedule: !schedule })}
                            >
                                Now
                            </Radio>
                            <Radio
                                checked={schedule}
                                onChange={() => this.setState({ now: !now, schedule: !schedule })}
                            >
                                Send at a specific time
                            </Radio>
                            {schedule && (
                                <div className="datepicker">
                                    <DatePicker
                                        timeIntervals={1}
                                        selected={this.state.startDate}
                                        onSelect={this.handleSelect}
                                        onChange={date => this.handleChange(date)}
                                        showTimeSelect
                                        timeFormat="HH:mm"
                                        dateFormat="MMMM d, yyyy h:mm aa"
                                        timeCaption="Time"
                                        peekNextMonth
                                        showMonthDropdown
                                        showYearDropdown
                                        dropdownMode="select"
                                        value={startDate}
                                    />
                                </div>
                            )}
                        </div>
                        {/* <div className="section">Show Count</div> */}
                        <ColoredButtons onClick={this.handleContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons
                            onClick={() => this.props.history.goBack()}
                            className="float grey-border"
                        >
                            Go Back
                        </TransparentButtons>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepThree)
