import React, { Component } from 'react'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { Radio } from '@/components/radio'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings/index'
import { CreateCampaignAction } from '@/actions/CreateCampaign/emailCampaign'

class StepOne extends Component {
    state = {
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview'],
        smsCampaign: true,
        name: '',
        nameError: false,
    }

    async componentDidMount() {
        const { search } = this.props.location
        let campaignId = null
        if (search && search.indexOf('id=') > -1) {
            campaignId = search.split('id=')[1]
        }
        this.setState({
            campaignId,
        })
        if (!campaignId) {
            createParentId()
        }
    }

    handleInputFields(key, value) {
        this.setState({
            [key]: value,
        })
    }

    handleContinue = () => {
        const { name } = this.state

        let anyError = false

        if (name === '') {
            this.setState({
                error: true,
            })
            anyError = true
        }
        if (!anyError) {
            const { steps, ...rest } = this.state
            const { parentID } = this.props.CreateCampaign.data
            CreateCampaignAction({ parentID, ...rest, next: 'step-2' })
        }
    }
    render() {
        const { steps, name, nameError, smsCampaign } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={0} />
                <div className="mrgn-btm">
                    <span>Channel :</span>
                    <span style={{ color: '#6cae53' }}>SMS</span>
                </div>

                <div className="section">
                    <SmallHeading>Campaign Name*</SmallHeading>
                    {nameError && <div className="alert">*This field is required</div>}
                    <input
                        placeholder="Enter Campaign Name"
                        type="text"
                        className="form-input"
                        name="name"
                        value={name}
                        onChange={e => this.handleInputFields('name', e.target.value)}
                    />
                </div>
                <div className="section">
                    <SmallHeading>SMS Type</SmallHeading>
                    <Radio checked={smsCampaign}>SMS Campaign</Radio>
                    {/* this function is  not active now */}
                    {/* <Radio
                        checked={connector}
                        onClick={() =>
                            this.setState({ connector: !connector, smsCampaign: !smsCampaign })
                        }
                    >
                        Connector Campaign
                    </Radio> */}
                    <h5 className="mrgn-top-15">
                        Create campaigns to transfer data to external APIs. You can use Connectors
                        to send message via other channels (SMS, Facebook messages), post user
                        action data to owned/3rd Party Analytics systems and sending audiences to Ad
                        Networks etc.
                    </h5>
                </div>
                <ColoredButtons as="link" to="/sms/step-2" className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons
                    onClick={() => this.props.history.goBack()}
                    className="float grey-border"
                >
                    Go Back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default StepOne
