import React, { Component } from 'react'
import 'emoji-mart/css/emoji-mart.css'
import { Picker } from 'emoji-mart'

class InputEmoji extends Component {
    state = {
        showEmojiPicker: false,
    }

    handleOnChange = e => {
        if (this.props.onChange) {
            this.props.onChange(e)
        }
    }

    renderIcon = () => {
        return (
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="24" height="24">
                <path d="M12 0C5.373 0 0 5.373 0 12s5.373 12 12 12 12-5.373 12-12S18.627 0 12 0m0 22C6.486 22 2 17.514 2 12S6.486 2 12 2s10 4.486 10 10-4.486 10-10 10" />
                <path d="M8 7a2 2 0 1 0-.001 3.999A2 2 0 0 0 8 7M16 7a2 2 0 1 0-.001 3.999A2 2 0 0 0 16 7M15.232 15c-.693 1.195-1.87 2-3.349 2-1.477 0-2.655-.805-3.347-2H15m3-2H6a6 6 0 1 0 12 0" />
            </svg>
        )
    }

    render() {
        const { showEmojiPicker } = this.state
        const { value, ...rest } = this.props

        return (
            <div>
                <input type="text" {...rest} value={value} onChange={this.handleOnChange} />
                <div className="flex-space-bt">
                    <span className="text-count">{value.length}/300</span>
                    <div
                        className="emoji-icon"
                        onClick={() => this.setState({ showEmojiPicker: !showEmojiPicker })}
                    >
                        {this.renderIcon()}
                    </div>
                </div>
                {showEmojiPicker && (
                    <div className="emoji-container">
                        <Picker />
                    </div>
                )}
            </div>
        )
    }
}

export default InputEmoji
