import React, { Component } from 'react'
import _ from 'lodash'
import connectComponent from '@/connect'
import WrapComponents from '@/components/wrapper'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import Loader from '@/components/loader'
import Alert from '@/components/alert'
import CreateSegementation from '../../Segmentation/createSegment'
import { getParentDetails } from '@/actions/CreateCampaign/PnCampaign'
import { UpdateCampaignBulk } from '@/actions/CreateCampaign/PnCampaign'
import { UploadContent } from '@/actions/utils'
import CSVIcon from '@/assets/images/csv-icon.svg'

class StepFour extends Component {
    state = {
        parentID: '',
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview'],
        uploadCsv: true,
        fileUploaded: false,
        createSegment: false,
        userFilePath: '',
        error: false,
        errorMessage: '',
        targetUsers: 0,
        filePreview: null,
    }

    async componentWillMount() {
        let {
            params: { parent_id },
        } = this.props.match
        parent_id = parseInt(parent_id, 10)
        this.setState({
            parentID: parent_id,
        })
        await getParentDetails(parent_id)
        this.getUserFilePath()
    }

    getUserFilePath = () => {
        const {
            CreateCampaign: { data },
        } = this.props
        this.setState({
            filePreview: this.getFileName(data),
        })
    }

    getFileName = campaing => {
        const userFilePath = _.get(campaing, 'userFilePath')
        if (userFilePath) {
            const lindex = userFilePath.lastIndexOf('/') + 1
            return {
                name: userFilePath.substr(lindex),
                file: userFilePath,
            }
        }
        return null
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    handleFileChange = async e => {
        const { files } = e.target
        if (files.length) {
            const res = await UploadContent(files[0], 'user_segment')
            if (res) {
                this.setState({
                    fileUploaded: true,
                    userFilePath: res.s3url,
                    targetUsers: res.targetUsers,
                    filePreview: {
                        name: files[0].name,
                        file: response.s3url,
                    },
                })
            }
        }
    }

    handleContinue() {
        const { data } = this.props.CreateCampaign
        const {
            userFilePath,
            fileUploaded,
            campaignId,
            targetUsers,
            filePreview,
            parentID,
        } = this.state
        let hasError = false

        if (!fileUploaded && filePreview) {
            return history.push(`/sms/step-5/${campaignId}`)
        }

        if (!targetUsers) {
            this.setState({
                error: true,
                errorMessage: 'Error: file is empty.',
            })
            hasError = true
        }

        if (!fileUploaded) {
            this.setState({
                error: true,
                errorMessage: 'Please upload user file containing user information',
            })
            hasError = true
        }

        if (!hasError) {
            const requests = []
            const next = `/in-app/step-5/${parentID}`
            if (_.isArray(data)) {
                _.forEach(data, part => {
                    requests.push({
                        ..._.assign({}, part, { userFilePath }),
                    })
                })
            }
            UpdateCampaignBulk(requests, next)

            // history.push(`/push-notification/step-5/${parentID}`)
        }
    }

    render() {
        const {
            steps,
            uploadCsv,
            createSegment,
            fileUploaded,
            error,
            targetUsers,
            errorMessage,
            filePreview,
        } = this.state
        const { CreateCampaign } = this.props

        if (CreateCampaign.loading) {
            return <Loader />
        }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={3} />
                {error && (
                    <Alert onClose={() => this.setState({ error: false })}>{errorMessage}</Alert>
                )}
                {CreateCampaign.error && <Alert>Error: {CreateCampaign.error}</Alert>}
                <div>
                    <div className="section-left">
                        <div className="section ">
                            <SmallHeading>Target Audience</SmallHeading>
                            <Radio
                                checked={uploadCsv}
                                onChange={() =>
                                    this.setState({
                                        uploadCsv: !uploadCsv,
                                        createSegment: !createSegment,
                                    })
                                }
                            >
                                Upload CSV
                            </Radio>
                            <Radio
                                checked={createSegment}
                                onChange={() =>
                                    this.setState({
                                        uploadCsv: !uploadCsv,
                                        createSegment: !createSegment,
                                    })
                                }
                            >
                                Create Segment
                            </Radio>
                            {createSegment && <CreateSegementation />}
                            {uploadCsv && (
                                <div className="section mrgn-top-15">
                                    <div className="upload-file-section">
                                        <SmallHeading>Upload CSV file</SmallHeading>
                                        <label htmlFor="uploadFile" className="">
                                            <input
                                                type="file"
                                                id="uploadFile"
                                                onChange={this.handleFileChange}
                                                accept=".csv"
                                                className="upload-file"
                                            />
                                        </label>
                                    </div>
                                    {fileUploaded && (
                                        <>
                                            <Alert className="success mrgn-top-15">
                                                File has been uploaded successfully
                                            </Alert>
                                            <SmallHeading className="success mrgn-top-15" />
                                            Number of users in files : {targetUsers}
                                        </>
                                    )}
                                    {filePreview && (
                                        <a
                                            className="file-uploaded"
                                            href={filePreview.file}
                                            target="_blank"
                                            rel="noopener nofollow"
                                        >
                                            <img
                                                className="file-icon"
                                                src={CSVIcon}
                                                alt="csv icon"
                                            />
                                            <div className="file-name">{filePreview.name}</div>
                                        </a>
                                    )}
                                </div>
                            )}
                        </div>
                        <ColoredButtons
                            className="float"
                            onClick={() => {
                                this.handleContinue()
                            }}
                        >
                            Continue
                        </ColoredButtons>
                        <TransparentButtons
                            onClick={() => this.props.history.goBack()}
                            className="float grey-border"
                        >
                            Go Back
                        </TransparentButtons>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default connectComponent(StepFour)
